import React from 'react';
import ProductsTable from './sections/ProductsTable';
import OrderDetails from './sections/OrderDetails';
import { MDBRow } from 'mdbreact';


class NewOrder extends React.Component {
  constructor() {
    super();

    this.state = {
      orderTotal: 0,
      totalValueAdded: 0
    };
    this.addProductToTbl = this.addProductToTbl.bind(this);
    this.deleteProduct = this.deleteProduct.bind(this);
    this.saveOrderDetailsToDB = this.saveOrderDetailsToDB.bind(this);
    this.saveOrderToDB = this.saveOrderToDB.bind(this);
    this.refreshOrdersTable = this.refreshOrdersTable.bind(this);
  }

  addProductToTbl = (pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked) => {
    console.log(pPrice);

    this.setState({
      orderTotal: this.state.orderTotal + parseInt(pPrice)
    })

    if (pChecked) {

      this.setState({
        totalValueAdded: this.state.totalValueAdded + parseInt(pPrice)
      })
      console.log(this.state.orderTotal);
      this.refs.orderDetails.set
    }
    this.refs.productsTable.addProductToTbl(pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked);
  }

  deleteProduct = (price, i, pChecked) => {

    this.refs.orderDetails.setTotalValue(price);
    if (pChecked === 'Yes') {
      this.refs.orderDetails.setTotalValueAdded(price);
    }
    let table = document.getElementById('productsTable');
    table.deleteRow(i);
    if (table.rows.length === 1) {
      this.refs.productsTable.setState({ submitOrderBtn: false })
    }
  }

  checkOrderDetailsValidity = () => {
    var orderDetailsValidity = this.refs.orderDetails.orderDetailsValidity();
    if (!orderDetailsValidity) {
      return false;
    }
    else if (this.customerForm.checkValidity() === false) {
      this.customerForm.classList.add('was-validated');
      return false;
    }
    else {
      return true;
    }

  }

  saveOrderDetailsToDB = (pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked) => {

    let orderId = this.orderId.value;
    let orderDetails = { order_id: orderId, pName: pName, pSKU: pSKU, pRate: pRate, pQTY: pQTY, pPrice: pPrice, pRemarks: pRemarks, pChecked: pChecked }
    var options = {
      method: 'POST',
      body: JSON.stringify(orderDetails),
      headers: { 'Content-Type': 'application/json' }
    }
    fetch('/addNewOrderDetails', options)
      .then((res) => res.json())
      .then((json) => {
        // console.log(json)
      })
      .catch((error) => console.log(error))
    console.log(pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked)
    this.refs.orderDetails.setTotalValue(pPrice);
    if (pChecked === 'Yes') {
      this.refs.orderDetails.setTotalValueAdded(pPrice);
    }
  }

  saveOrderToDB = () => {
    this.refs.orderDetails.saveOrderToDB();
  }

  refreshOrdersTable = () => {
    window.location.reload();
  }
  calculatingOrderTotal = () => {
    let orderTotal = 0;
    let x = document.getElementById('productsTable')
    if (x !== null) {

      if (x.rows.length !== null && x.rows.length !== 1) {

        for (var index = 1; index < x.rows.length; index++) {
          let z = parseInt(x.rows[index].cells[5].innerHTML)
          orderTotal += z
        }
      };
    }
    return (orderTotal);
  }
  minusFromTotal = (pPrice) => {
    this.setState({
      orderTotal: this.state.orderTotal - parseInt(pPrice)
    })
  }

  render() {

    return (
      <MDBRow style={{ marginTop: '78px' }}>
        <OrderDetails
          ref='orderDetails'
          orderId={el => (this.orderId = el)}
          orderForm={el => (this.orderForm = el)}
          customerForm={el => (this.customerForm = el)}
          addProductToTbl={this.addProductToTbl}
          orderTotal={this.state.orderTotal}
          totalValueAdded={this.state.totalValueAdded}
        />
        <MDBRow center className='mt-3 mx-auto'>

          <ProductsTable
            ref='productsTable'
            refreshOrdersTable={this.refreshOrdersTable}
            deleteProduct={this.deleteProduct}
            saveOrderDetailsToDB={this.saveOrderDetailsToDB}
            saveOrderToDB={this.saveOrderToDB}
            checkOrderDetailsValidity={this.checkOrderDetailsValidity}
            productsTable={el => (this.productsTable = el)}
            minusFromTotal={this.minusFromTotal}
          />
        </MDBRow>
      </MDBRow>
    );
  }
}

export default NewOrder;
