import React, { Component } from 'react';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter, MDBRow, MDBCol, MDBInput, MDBDataTable } from 'mdbreact';


class EditOrderModal extends Component {
    order = '';
    constructor(props) {
        super(props);
        this.state = {
            modalShow: false,
            order: '',
            orderTotal: 0,
            totalValueAdded: 0,
            orderSource: '',
            orderStatus: '',
            orderTracking: '',
            orderNote: '',
            cName: '',
            cContact: '',
            cAddress: '',
        }
    }
    fetchData = (id) => {

        fetch('/getSpecificOrder/' + id)
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
                // this.order = json.data
                var order = json.data
                this.setState({
                    order: order,
                    orderTotal: order.total,
                    totalValueAdded: order.total_value_added,
                    orderSource: order.source,
                    orderStatus: order.status,
                    orderTracking: order.tracking_id,
                    orderNote: order.note,
                })
            })
            .catch((error) => console.log(error))

        fetch('/getAllOrderDetails/' + id)
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
                json.data.map((x) => {
                    delete x.createdAt;
                    delete x.updatedAt;
                    delete x.deletedAt;
                    delete x.id;
                    delete x.order_id;
                });
                this.setState({
                    orderDetails: json.data
                })

            })
            .catch((error) => console.log(error))
    }

    toggle = () => {
        this.setState({
            modalShow: !this.state.modalShow,

        });
    }
    render() {
        console.log(this.state.order);

        var { order, orderDetails } = this.state;

        var data = {
            columns: [
                { label: 'Name', field: 'product_name' },
                { label: 'SKU', field: 'product_sku' }, { label: 'Rate', field: 'product_rate' },
                { label: 'QTY', field: 'product_qty' }, { label: 'Price', field: 'product_price' },
                { label: 'Remarks', field: 'product_remarks' }, { label: 'Extra Added', field: 'product_extra_added' },
            ],
            rows: orderDetails
        }
        return (
            <MDBContainer>
                <MDBModal isOpen={this.state.modalShow} toggle={this.toggle} size='lg' centered>
                    <MDBModalHeader toggle={this.toggle}>Order details</MDBModalHeader>
                    <MDBModalBody>
                        <MDBRow center>
                            <MDBCol md="9" className='m-0 p-0'>
                                <MDBRow center className='m-0 p-0' >
                                    <MDBCol sm='6' className='m-0'>
                                        <MDBInput value={this.state.orderTotal} label="Total" hint="Total" disabled className='m-0' style={{ fontWeight: 'bold' }} />
                                    </MDBCol>
                                    <MDBCol sm='6' className='m-0'>
                                        <MDBInput value={this.state.totalValueAdded} className='m-0' style={{ fontWeight: 'bold' }} label="Total value added" hint="Total value added" disabled />
                                    </MDBCol>
                                </MDBRow>
                                <MDBRow center className='m-0 p-0' >
                                    <MDBCol sm='4' className='m-0 ' >
                                        <MDBInput value={order.id} label="Id" outline disabled className='mt-0 mb-0' />
                                    </MDBCol>
                                    <MDBCol md='4' className=' ' middle >
                                        <React.Fragment>
                                            <Select
                                                styles={customStyles}
                                                value={orderSource}
                                                onChange={this.handleSelectChange}
                                                options={sourceOptions}
                                                placeholder='Source'
                                                isSearchable
                                                className='my-auto'
                                                isClearable
                                            />
                                        </React.Fragment>
                                    </MDBCol>
                                    <MDBCol md='4' className='m-0 ' middle >
                                        <React.Fragment>
                                            <CreateableSelect
                                                ref='createableSelect'
                                                orderStatus={this.orderStatus}
                                                isValidStatus={isValidStatus}
                                            />
                                        </React.Fragment>
                                    </MDBCol>
                                </MDBRow>
                                <MDBRow center className='m-0 p-0' >
                                    <MDBCol sm='4' className='m-0 '>
                                        <MDBInput label="Tracking Id" value={order.tracking_id} onInput={this.handleInput} outline className='m-0' />
                                    </MDBCol>
                                    <MDBCol sm='8' className='m-0 '>
                                        <MDBInput label="Note" value={order.note} onInput={this.handleInput} outline className='m-0' />
                                    </MDBCol>
                                </MDBRow>
                                <MDBRow className='m-0 p-0' end>
                                    <MDBCol sm='12' className='m-0 p-0' bottom >
                                        <label style={{ fontWeight: 'bold', textAlign: 'left' }}>Customer:</label>
                                    </MDBCol>
                                </MDBRow>
                                <MDBRow className='m-0 p-0'>
                                    <MDBCol sm='3' className='m-0'>
                                        <MDBInput value={order.customer_name} className='m-0' label="Name" outline />
                                    </MDBCol>
                                    <MDBCol sm='3' className='m-0'>
                                        <MDBInput value={order.customer_contact} className='m-0' label="Contact" outline />
                                    </MDBCol>
                                    <MDBCol sm='6' className='m-0'>
                                        <MDBInput value={order.customer_address} className='m-0' label="Address" outline />
                                    </MDBCol>

                                </MDBRow>
                                <MDBRow className='m-0 p-0' end>
                                    <MDBCol sm='12' className='m-0 p-0' bottom >
                                        <label style={{ fontWeight: 'bold', textAlign: 'left' }}>Products:</label>
                                    </MDBCol>
                                </MDBRow>
                                <MDBDataTable id='viewOrdersTable' striped small theadColor="teal"
                                    bordered entries={10} entriesOptions={[5, 10, 20]} responsive
                                    data={data} theadTextWhite >
                                </MDBDataTable>


                            </MDBCol>
                        </MDBRow>
                    </MDBModalBody>
                    <MDBModalFooter>
                        <MDBBtn color="secondary" onClick={this.toggle}>Close</MDBBtn>
                        <MDBBtn outline color="primary">Save changes</MDBBtn>
                    </MDBModalFooter>
                </MDBModal>
            </MDBContainer>
        );
    }
}

export default EditOrderModal;
