import React, { Component } from 'react';
import { MDBDataTable, MDBCard, MDBCardHeader, MDBCardBody, MDBBtn, MDBIcon } from 'mdbreact';
import { Link } from 'react-router-dom'
import ViewOrderModal from './sections/ViewOrderModal';
import EditOrderModal from './sections/EditOrderModal';
import DeleteOrderModal from './sections/DeleteOrderModal';


class AllUsers extends Component {
    _isMounted = false
    constructor() {
        super();
        this._isMounted = true
        fetch('/getAllUsers')
            .then((res) => res.json())
            .then((json) => {
                console.log(json)
                if (this._isMounted) {

                    this.setState({ users: json.data })
                }
            })
            .catch((error) => console.log(error))
        this.state = {
            users: [],
        }
    }

    componentWillUnmount = () => {
        this._isMounted = false
    }

    handleView = (e) => {
        // console.log('view');
        var iconClik = e.target.parentNode.parentNode.parentNode.parentNode;
        var svgClik = e.target.parentNode.parentNode.parentNode;
        var buttonClik = e.target.parentNode.parentNode;
        var tdClik = e.target.parentNode;
        var targt = e.target

        if (iconClik.rowIndex === undefined) {
            iconClik = svgClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = buttonClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = tdClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = targt;
        }
        let id = iconClik.cells[1].innerHTML
        this.refs.viewOrderModal.setState({
            // orderId: id,
            modalShow: true
        })
        this.refs.viewOrderModal.fetchData(id);
    }

    handleEdit = (e) => {
        // console.log('edit');
        var iconClik = e.target.parentNode.parentNode.parentNode.parentNode;
        var svgClik = e.target.parentNode.parentNode.parentNode;
        var buttonClik = e.target.parentNode.parentNode;
        var tdClik = e.target.parentNode;
        var targt = e.target

        if (iconClik.rowIndex === undefined) {
            iconClik = svgClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = buttonClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = tdClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = targt;
        }
        let id = iconClik.cells[1].innerHTML

        this.refs.editOrderModal.setState({
            modalShow: true
        })
        this.refs.editOrderModal.fetchData(id);

    }

    handleDelete = (e) => {
        var iconClik = e.target.parentNode.parentNode.parentNode.parentNode;
        var svgClik = e.target.parentNode.parentNode.parentNode;
        var buttonClik = e.target.parentNode.parentNode;
        var tdClik = e.target.parentNode;
        var targt = e.target

        if (iconClik.rowIndex === undefined) {
            iconClik = svgClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = buttonClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = tdClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = targt;
        }
        var i = iconClik.rowIndex;
        let dRowValue = iconClik.cells[1].innerHTML

        this.refs.deleteOrderModal.setState({
            modalShow: true,
            rowToBeDeleted: i,
            dRowValue: dRowValue

        })
    }

    render() {
        var { users } = this.state;
        var rows = [];
        var index = 0;
        console.log(users);

        users.forEach((user) => {

            index = index + 1;
            rows.push(
                {
                    index: index,
                    id: user.id,
                    name: user.name,
                    email: user.email,
                    cell: user.cell,
                    username: user.username,
                    // password: user.password,
                    role_id: user.role_id,
                    buttons: <React.Fragment>
                        <MDBBtn style={{ fontSize: '15px' }} onClick={this.handleView} className='m-1 py-1 px-2' outline color='info' size="sm"><MDBIcon icon="eye" /></MDBBtn>
                        <MDBBtn style={{ fontSize: '15px' }} onClick={this.handleEdit} className='m-1 py-1 px-2' outline color='teal' size="sm"><MDBIcon icon="pencil-alt" /></MDBBtn>
                        <MDBBtn style={{ fontSize: '15px' }} onClick={this.handleDelete} className='m-1 py-1 px-2' outline color='red darken-3' size="sm"><MDBIcon icon="trash" /></MDBBtn>
                    </React.Fragment>
                }
            );
        });

        var data = {
            columns: [
                { label: '#', field: 'index',style:'' }, { label: 'Id', field: 'id', }, { label: 'Name', field: 'name' },
                { label: 'Email', field: 'email', }, { label: 'Cell', field: 'cell', },
                { label: 'Username', field: 'username', }, { label: 'Role', field: 'role_id', },
                { label: 'Action', field: 'buttons' }
            ],
            rows: rows
        }
        return (

            <MDBCard className=' p-0' style={{ marginTop: '70px' }}>
                <MDBCardHeader tag="h4" className="text-center font-weight-bold">
                    Users
                </MDBCardHeader>
                <MDBCardBody className='p-2'>

                    <MDBDataTable id='ordersTable' striped small hover theadColor="teal"
                        bordered btn entries={12} entriesOptions={[5, 10, 20, 35, 55, 70, 100, 135]} responsive
                        data={data} theadTextWhite >
                    </MDBDataTable>
                    <ViewOrderModal
                        ref='viewOrderModal'
                    />
                    <EditOrderModal
                        ref='editOrderModal'
                    />
                    <DeleteOrderModal
                        ref='deleteOrderModal'
                    />
                </MDBCardBody>
            </MDBCard>
        );
    }

}

export default AllUsers
