import React from 'react';
import ProductsTable from './sections/ProductsTable';
import OrderDetails from './sections/OrderDetails';


class Home extends React.Component {
  constructor(props) {
    super(props);

    this.state = {

    };

    this.handleAddProduct = this.handleAddProduct.bind(this);
    this.deleteProduct = this.deleteProduct.bind(this);
  }

  deleteProduct = (price, i, pChecked) => {
    let orderTotal = this.refs.orderDetails.orderTotal;
    let totalValueAdded = this.refs.orderDetails.totalValueAdded;
    orderTotal = orderTotal - price;
    if (pChecked == 'Yes') {
      totalValueAdded = totalValueAdded - price
    }
    let table = document.getElementById('productsTable');
    table.deleteRow(i);
    if (table.rows.length === 1) {
      this.refs.productsTable.setState({ submitOrderBtn: false })
    }
  }




  render() {

    return (
      <div className="" style={{ marginTop: '70px' }}>

        <OrderDetails
          ref='orderDetails'
          addProductToTbl={this.addProductToTbl}
        />
        <ProductsTable
          ref='productsTable'
          deleteProduct={this.deleteProduct}
        />

      </div>
    );
  }
}

export default Home;
