import React from 'react';
import { MDBInput, MDBFormInline, MDBBtn, MDBDropdown, MDBDropdownToggle, MDBDropdownMenu, MDBDropdownItem } from 'mdbreact';

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.orderId = '';
    this.state = {
      orderId: '',
      orderSource: '',
      orderStatus: '',
      orderTracking: '',
      cName: '',
      cContact: '',
      cAddress: '',
      pName: '',
      pSKU: '',
      pRate: '',
      pQTY: '',
      pRemarks: '',

    };
    this.addProduct = this.addProduct.bind(this);
    this.orderTotal = this.orderTotal.bind(this);
    this.totalValueAdded = this.totalValueAdded.bind(this);
    this.handleInput = this.handleInput.bind(this);
  }
  orderTotal = () => {

  }
  totalValueAdded = () => {

  }
  handleInput = e => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }
  addProduct = () => {
    // e.preventDefault();
    let productForm = this.refs.productForm;
    if (productForm.checkValidity() === false) {
      productForm.classList.add('was-validated');
    }
    else {
      console.log('pForm is ok')
      // console.log('login bnt ok' + this.state.username + '' + this.state.password)
      // this.setState({ isLoggedIn: true })
      // this.props.history.push('/home')
      // this.saveToServer();
    }

  }

  render() {

    return (
      <div className="mb-4" style={{ marginTop: '70px' }}>

        <h2 className="h1-responsive text-center my-4">Order management</h2>
        <form ref='mainForm'>

          <MDBFormInline ref='orderForm'>
            <div className=' p-0' >
              <label className='form-control' style={{ fontWeight: 'bold', textAlign: 'left' }}>Order:</label>
            </div>
            <MDBInput value={this.fetchOrderId()} label="Id" outline disabled style={{ marginRight: '4px' }} />
            <select name='orderSource' value={this.state.orderSource} onChange={this.handleInput} className=" custom-select" required>
              <option value='' disabled>--Source--</option>
              <option value='1' >Option 1</option>
              <option value='2' >Option 2</option>
              <option value='3' >Option 3</option>
            </select>
            <select name='orderStatus' value={this.state.orderStatus} onChange={this.handleInput} className=" custom-select" required>
              <option value='' disabled>--Status--</option>
              <option value='1' >Option 1</option>
              <option value='2' >Option 2</option>
              <option value='3' >Option 3</option>
            </select>
            <MDBInput label="Tracking Id" name="orderTracking" onInput={this.handleInput} value={this.state.ord} outline style={{ marginRight: '4px' }} />
            <MDBInput value={this.orderTotal()} label="Total" outline disabled style={{ marginRight: '4px' }} />
          </MDBFormInline>

          <MDBFormInline ref='customerForm'>
            <div className=' p-0' >
              <label className='form-control' style={{ fontWeight: 'bold', textAlign: 'left' }}>Customer:</label>
            </div>
            <MDBInput value={this.state.cName} style={{ marginRight: '4px' }} label="Name" name="cName" onInput={this.handleInput} required validate outline />
            <MDBInput value={this.state.cContact} style={{ marginRight: '4px' }} label="Contact" name="cContact" onInput={this.handleInput} required validate outline />
            <MDBInput value={this.state.cAddress} style={{ marginRight: '4px' }} label="Address" name="cAddress" onInput={this.handleInput} required validate outline />
            <MDBInput value={this.totalValueAdded()} style={{ marginRight: '4px' }} label="Total value added" outline disabled />
          </MDBFormInline>

          <MDBFormInline ref='productForm'>
            <div className='p-0' >
              <label className='form-control' style={{ fontWeight: 'bold', textAlign: 'left' }}>Product:</label>
            </div>
            <MDBInput  type='text' label="Name" name="pName" onInput={this.handleInput} required validate outline style={{ marginRight: '4px' }}/>
            <MDBInput  type='text' label="SKU" name="pSKU" onInput={this.handleInput} outline style={{ marginRight: '4px' }}/>
            <MDBInput  type='number' label="Rate" name="pRate" onInput={this.handleInput} required validate outline min='0' style={{ marginRight: '4px' }}/>
            <MDBInput  type='number' label="Qty" name="pQTY" onInput={this.handleInput} required validate outline style={{ marginRight: '4px' }}/>
            <MDBInput  type='number' label="Price" name='pPrice' disabled outline style={{ marginRight: '4px' }}/>
            <MDBInput  type='text' label="Remarks" name="pRemarks" onInput={this.handleInput} outline style={{ marginRight: '4px' }}/>
            <MDBInput type='checkbox' filled label='Is Extra added?' />
            <MDBBtn onClick={this.addProduct} size='lg' style={{ width: '50px', height: '40px', padding: '0px' }} outline>Add</MDBBtn>
          </MDBFormInline>
        </form>

      </div>
    );
  }
}

export default Home;
