import React, { Component } from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput, MDBIcon } from 'mdbreact';
import Select from 'react-select';


const roleOptions = [
    { value: 'admin', label: 'Admin' },
    { value: 'operator', label: 'Operator' }
];

class NewUser extends Component {
    constructor() {
        super();
        this.state = {
            isValidRole: true,
            role: { label: 'Operator', value: 'operator' },
            name: '',
            email: '',
            cell: '',
            username: '',
            password: ''
        };
    }

    handleSelectChange = selectedOption => {
        if (selectedOption === null) {
            this.setState({
                role: selectedOption, isValidRole: false
            })
        }
        else {
            this.setState({
                role: selectedOption, isValidRole: true
            })
        }
    }

    handleInput = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        console.log(this.state.name, this.state.email, this.state.username, this.state.password,
            this.state.role, this.state.cell, this.state.isValidRole);

    }

    render() {
        const { role, isValidRole } = this.state
        const customStyles = {
            control: (base, state) => ({
                ...base,
                borderColor: state.isFocused ?
                    '#ddd' : isValidRole ?
                        '#ddd' : 'red',
                width: '191px',
            })
        }


        return (

            <MDBContainer className='p-0' style={{ border: '1px solid', marginTop: '80px' }}>
                <MDBRow center>
                    <MDBCol md="6">
                        <form onSubmit={this.handleSubmit} noValidate>
                            <p className="h5 text-center mb-4">New User</p>
                            <div className="grey-text">
                                <MDBInput
                                    onInput={this.handleInput}
                                    label="Name"
                                    name='name'
                                    icon="user"
                                    group
                                    type="text"
                                    validate
                                    error="wrong"
                                    success="right"
                                    required
                                />
                                <MDBInput
                                    onInput={this.handleInput}
                                    label="Email"
                                    name="email"
                                    icon="envelope"
                                    group
                                    type="email"
                                    validate
                                    error="wrong"
                                    success="right"
                                    required
                                />
                                <MDBInput
                                    onInput={this.handleInput}
                                    label="Phone"
                                    name="cell"
                                    icon="phone"
                                    group
                                    type="text"
                                    validate
                                    error="wrong"
                                    success="right"
                                />
                                <MDBInput
                                    onInput={this.handleInput}
                                    label="Username"
                                    name="Username"
                                    icon="user"
                                    group
                                    type="text"
                                    validate
                                    required
                                />
                                <MDBInput
                                    onInput={this.handleInput}
                                    label="Password"
                                    icon="lock"
                                    group
                                    type="password"
                                    validate
                                    required
                                />
                                {/* <MDBIcon icon="home" style={{float:'left'}} /> */}
                                <Select
                                    styles={customStyles}
                                    defaultValue={roleOptions[1]}
                                    // value={role}
                                    onChange={this.handleSelectChange}
                                    options={roleOptions}
                                    placeholder='Role'
                                    isSearchable
                                    isClearable
                                />
                            </div>
                            <div className="text-right">
                                <MDBBtn size='sm' color="teal" outline type='submit'>Register</MDBBtn>
                            </div>
                        </form>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>

        );
    }
}


export default NewUser
