import React, { Component } from 'react';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter, MDBRow, MDBCol, MDBInput, MDBDataTable } from 'mdbreact';
import Select from 'react-select';
import CreateableSelect from './CreatableAdvancedSelect';
import OrderDetails from './OrderDetails';
import ProductsTable from './ProductsTable'

class EditOrderModal extends Component {
    order = '';
    constructor(props) {
        super(props);
        this.state = {
            modalShow: false,
            order: '',
            orderTotal: 0,
            totalValueAdded: 0,
            orderSource: '',
            orderStatus: '',
            orderTracking: '',
            orderNote: '',
            cName: '',
            cContact: '',
            cAddress: '',
            orderId: 1,
            showOrderDetails: false
        }
    }
    fetchData = (id) => {

        fetch('/getSpecificOrder/' + id)
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
                // this.order = json.data
                var order = json.data
                this.refs.orderDetails.setState({
                    order: order,
                    orderId: order.id,
                    orderTotal: order.total,
                    totalValueAdded: order.total_value_added,
                    orderSource: order.source,
                    orderStatus: order.status,
                    orderTracking: order.tracking_id,
                    orderNote: order.note,
                    cName: order.customer_name,
                    cContact: order.customer_contact,
                    cAddress: order.customer_address,
                    // showOrderDetails: true,
                })
            })
            .catch((error) => console.log(error))

        fetch('/getAllOrderDetails/' + id)
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
                json.data.map((x) => {
                    delete x.createdAt;
                    delete x.updatedAt;
                    delete x.deletedAt;
                    delete x.id;
                    delete x.order_id;
                });
                this.setState({
                    orderDetails: json.data
                })

            })
            .catch((error) => console.log(error))
    }

    toggle = () => {
        this.setState({
            modalShow: !this.state.modalShow,

        });
    }
    // handleInput = e => {
    //     this.setState({
    //         [e.target.name]: e.target.value
    //     })
    // }
    addProductToTbl = (pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked) => {
        this.refs.productsTable.addProductToTbl(pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked);
    }

    deleteProduct = (price, i, pChecked) => {

        this.refs.orderDetails.minusTotalValue(price);
        if (pChecked === 'Yes') {
            this.refs.orderDetails.minusTotalValueAdded(price);
        }
        let table = document.getElementById('productsTable');
        table.deleteRow(i);
        if (table.rows.length === 1) {
            this.refs.productsTable.setState({ submitOrderBtn: false })
        }
    }

    checkOrderDetailsValidity = () => {
        var orderDetailsValidity = this.refs.orderDetails.orderDetailsValidity();
        if (!orderDetailsValidity) {
            return false;
        }
        else if (this.customerForm.checkValidity() === false) {
            this.customerForm.classList.add('was-validated');
            return false;
        }
        else {
            return true;
        }

    }

    saveOrderDetailsToDB = (pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked) => {

        let orderId = this.orderId.value;
        let orderDetails = { order_id: orderId, pName: pName, pSKU: pSKU, pRate: pRate, pQTY: pQTY, pPrice: pPrice, pRemarks: pRemarks, pChecked: pChecked }
        var options = {
            method: 'POST',
            body: JSON.stringify(orderDetails),
            headers: { 'Content-Type': 'application/json' }
        }
        fetch('/addNewOrderDetails', options)
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
            })
            .catch((error) => console.log(error))
        // console.log(pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked)
        this.refs.orderDetails.minusTotalValue(pPrice);
        if (pChecked === 'Yes') {
            this.refs.orderDetails.minusTotalValueAdded(pPrice);
        }
    }

    saveOrderToDB = () => {
        this.refs.orderDetails.saveOrderToDB();
    }

    refreshOrdersTable = () => {
        window.location.reload();
    }

    minusFromTotal = (pPrice) => {
        this.refs.orderDetails.minusTotalValue(pPrice)
    }
    addToTotal = (pPrice) => {
        this.refs.orderDetails.addTotalValue(pPrice)
    }
    minusFromTotalValueAdded = (pPrice) => {
        this.refs.orderDetails.minusTotalValueAdded(pPrice);
    }
    addToTotalValueAdded = (pPrice) => {
        this.refs.orderDetails.addTotalValueAdded(pPrice);
    }

    render() {
        
        return (
            <MDBContainer>
                <MDBModal isOpen={this.state.modalShow} toggle={this.toggle} size='lg' centered>
                    <MDBModalHeader toggle={this.toggle}>Order details</MDBModalHeader>
                    <MDBModalBody>
                       
                        <MDBRow center>
                            <MDBCol md="9" className='m-0 p-0'>
                                <MDBRow center className='m-0 p-0' >
                                    <MDBCol sm='6' className='m-0'>
                                        <MDBInput value={this.state.orderTotal} label="Total" hint="Total" disabled className='m-0' style={{ fontWeight: 'bold' }} />
                                    </MDBCol>
                                    <MDBCol sm='6' className='m-0'>
                                        <MDBInput value={this.state.totalValueAdded} className='m-0' style={{ fontWeight: 'bold' }} label="Total value added" hint="Total value added" disabled />
                                    </MDBCol>
                                </MDBRow>
                                <MDBRow center className='m-0 p-0' >
                                    <MDBCol sm='4' className='m-0 ' >
                                        <MDBInput value={order.id} label="Id" outline disabled className='mt-0 mb-0' />
                                    </MDBCol>
                                    <MDBCol md='4' className=' ' middle >
                                        <React.Fragment>
                                            <Select
                                                styles={customStyles}
                                                value={orderSource}
                                                onChange={this.handleSelectChange}
                                                options={sourceOptions}
                                                placeholder='Source'
                                                isSearchable
                                                className='my-auto'
                                                isClearable
                                            />
                                        </React.Fragment>
                                    </MDBCol>
                                    <MDBCol md='4' className='m-0 ' middle >
                                        <React.Fragment>
                                            <CreateableSelect
                                                ref='createableSelect'
                                                orderStatus={this.orderStatus}
                                                isValidStatus={isValidStatus}
                                            />
                                        </React.Fragment>
                                    </MDBCol>
                                </MDBRow>
                                <MDBRow center className='m-0 p-0' >
                                    <MDBCol sm='4' className='m-0 '>
                                        <MDBInput label="Tracking Id" value={order.tracking_id} name="orderTracking" onInput={this.handleInput} outline className='m-0' />
                                    </MDBCol>
                                    <MDBCol sm='8' className='m-0 '>
                                        <MDBInput label="Note" value={order.note} name="orderNote" onInput={this.handleInput} outline className='m-0' />
                                    </MDBCol>
                                </MDBRow>
                                <MDBRow className='m-0 p-0' end>
                                    <MDBCol sm='12' className='m-0 p-0' bottom >
                                        <label style={{ fontWeight: 'bold', textAlign: 'left' }}>Customer:</label>
                                    </MDBCol>
                                </MDBRow>
                                <MDBRow className='m-0 p-0'>
                                    <MDBCol sm='3' className='m-0'>
                                        <MDBInput value={this.state.cName} onInput={this.handleInput} name="cName" className='m-0' label="Name" outline required />
                                    </MDBCol>
                                    <MDBCol sm='3' className='m-0'>
                                        <MDBInput value={this.state.cContact} onInput={this.handleInput} name="cContact" className='m-0' label="Contact" outline required />
                                    </MDBCol>
                                    <MDBCol sm='6' className='m-0'>
                                        <MDBInput value={this.state.cAddress} onInput={this.handleInput} name="cAddress" className='m-0' label="Address" outline required />
                                    </MDBCol>

                                </MDBRow>
                                <MDBRow className='m-0 p-0' end>
                                    <MDBCol sm='12' className='m-0 p-0' bottom >
                                        <label style={{ fontWeight: 'bold', textAlign: 'left' }}>Products:</label>
                                    </MDBCol>
                                </MDBRow>
                                <MDBDataTable id='viewOrdersTable' striped small theadColor="teal"
                                    bordered entries={10} entriesOptions={[5, 10, 20]} responsive
                                    data={data} theadTextWhite >
                                </MDBDataTable>


                            </MDBCol>
                        </MDBRow>
                    </MDBModalBody>
                    <MDBModalFooter>
                        <MDBBtn color="secondary" onClick={this.toggle}>Close</MDBBtn>
                        <MDBBtn outline color="primary">Save changes</MDBBtn>
                    </MDBModalFooter>
                </MDBModal>
            </MDBContainer>
        );
    }
}

export default EditOrderModal;
