import React, { Component } from 'react';
import { MDBInput, MDBFormInline, MDBBtn, MDBCard, MDBCardHeader, MDBCardBody } from 'mdbreact';
import Select from 'react-select';


const sourceOptions = [
    { value: '', label: 'option1' },
    { value: '', label: 'option2' },
    { value: '', label: 'option3' },
    { value: '', label: 'option4' },
];
const statusOptions = [
    { value: 'pending', label: 'Pending' },
    { value: 'dispatched', label: 'Dispatched' },
    { value: 'completed', label: 'Completed' },
];
class OrderDetails extends Component {
    constructor(props) {
        super(props);
        this.orderId = '';
        this.state = {
            orderSource: null,
            orderStatus: null,
            orderTracking: '',
            orderTotal: 0,
            totalValueAdded: 0,
            cName: '',
            cContact: '',
            cAddress: '',
            pName: '',
            pSKU: '',
            pRate: '',
            pQTY: '',
            pRemarks: '',
        }
        // this.handleSelectChange = this.handleSelectChange.bind(this);
        this.handleInput = this.handleInput.bind(this);
        this.pTotal = this.pTotal.bind(this);
        this.fetchOrderId = this.fetchOrderId.bind(this);
        this.handleAddProduct = this.handleAddProduct.bind(this);

    }
    handleSelectChange = name => sele => {
        // console.log(name,value);
        
        this.setState({ [name]: value })
        console.log(this.state.orderSource);
        console.log(this.state.orderStatus);
        

    }

    setValue = (a, value) => {
        console.log(a, value);

        this.setState({
            [a]: this.state[a] - parseInt(value)
        })
    }

    fetchOrderId = () => {

    }

    pTotal = () => {
        let total = this.state.pRate * this.state.pQTY;
        return total;
    }

    handleInput = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleAddProduct = (e) => {
        e.preventDefault();
        let form = this.refs.productForm;
        if (form.checkValidity() === false) {
            form.classList.add('was-validated');
        }
        else {
            let pName = this.state.pName;
            let pSKU = this.state.pSKU;
            let pRate = this.state.pRate;
            let pQTY = this.state.pQTY
            let pPrice = this.pPrice.value;
            let pRemarks = this.state.pRemarks;
            let pChecked = (this.checkbox.checked) ? true : false
            this.props.addProductToTbl(pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked);
            this.setState({ orderTotal: this.state.orderTotal + parseInt(pPrice) })
            if (pChecked) {
                this.setState({ totalValueAdded: this.state.totalValueAdded + parseInt(pPrice) })
            }
            this.setState({
                pName: '',
                pSKU: '',
                pRate: '',
                pQTY: '',
                pRemarks: ''
            })
            this.checkbox.checked = false;
        }
    }



    render() {


        return (
            <MDBCard className='col-md-12 p-0'>
                <MDBCardHeader tag="h3" className="text-center font-weight-bold py-1">
                    Order Details
                </MDBCardHeader>
                <MDBCardBody className='p-2'>
                    <div ref='mainForm' className='m-0 '>

                        <form className='form-inline mb-0 mt-0 ml-3' ref={this.props.orderForm}>
                            <div className=' p-0 mr-4' >
                                <label className='' style={{ fontWeight: 'bold', textAlign: 'left' }}>Order:</label>
                            </div>

                            <MDBInput value={this.fetchOrderId()} label="Id" outline disabled className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                            <div style={{ marginRight: '4px', width: '207px' }}>
                                <Select
                                    value={this.state.orderSource}
                                    onChange={this.handleSelectChange('orderSource')}
                                    options={sourceOptions}
                                    name='orderSource'
                                    placeholder='Source'
                                    isSearchable
                                    className='mt-0 mb-0'
                                    required
                                    validate
                                />
                            </div>
                            <div style={{ width: '207px', marginRight: '4px' }}>
                                <Select
                                    value={this.state.orderStatus}
                                    onChange={this.handleSelectChange('orderStatus')}
                                    options={statusOptions}
                                    name='orderStatus'
                                    placeholder='Status'
                                    isSearchable
                                    className='mt-0 mb-0'
                                    required
                                    validate
                                />
                            </div>
                            <MDBInput label="Tracking Id" name="orderTracking" onInput={this.handleInput} value={this.state.ord} outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                            <MDBInput value={this.state.orderTotal} label="Total" hint="Total" outline disabled className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                            <MDBInput value={this.state.totalValueAdded} className='mt-0 mb-0' style={{ marginRight: '4px' }} label="Total value added" hint="Total value added" outline disabled />
                        </form>

                        <form className='form-inline mb-0 mt-0 ml-3' ref={this.props.customerForm}>
                            <div className=' p-0 mr-4' >
                                <label className='' style={{ fontWeight: 'bold', textAlign: 'left' }}>Customer:</label>
                            </div>

                            <MDBInput value={this.state.cName} className='mt-0 mb-0' style={{ marginRight: '4px' }} label="Name" name="cName" onInput={this.handleInput} required validate outline />
                            <MDBInput value={this.state.cContact} className='mt-0 mb-0' style={{ marginRight: '4px' }} label="Contact" name="cContact" onInput={this.handleInput} required validate outline />
                            <MDBInput value={this.state.cAddress} className='mt-0 mb-0' style={{ marginRight: '4px' }} label="Address" name="cAddress" onInput={this.handleInput} required validate outline />
                        </form>
                        <form className='form-inline mb-0 mt-0 ml-3' ref='productForm' onSubmit={this.handleAddProduct} noValidate>

                            <div className='p-0 mr-4' >
                                <label className='' style={{ fontWeight: 'bold', textAlign: 'left' }}>Product:</label>
                            </div>

                            <MDBInput value={this.state.pName} type='text' label="Name" name="pName" onInput={this.handleInput} required validate outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                            <MDBInput value={this.state.pSKU} type='text' label="SKU" name="pSKU" onInput={this.handleInput} outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                            <MDBInput value={this.state.pRate} type='number' label="Rate" name="pRate" onInput={this.handleInput} required validate outline min='0' className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                            <MDBInput value={this.state.pQTY} type='number' label="Qty" name="pQTY" onInput={this.handleInput} required validate outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                            <MDBInput value={this.pTotal()} type='number' label='Price' hint="Price" inputRef={e => { this.pPrice = e }} disabled outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                            <MDBInput value={this.state.pRemarks} type='text' label="Remarks" name="pRemarks" onInput={this.handleInput} outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                            <MDBInput type='checkbox' inputRef={e => { this.checkbox = e }} className='mt-0 mb-0' filled label='Is Extra added?' />
                            <MDBBtn type='submit' size='lg' style={{ width: '50px', height: '40px', padding: '0px', margin: '0px' }} outline>Add</MDBBtn>
                        </form>
                    </div>
                </MDBCardBody>
            </MDBCard>
        );
    }

}

export default OrderDetails
