import React from 'react';
import { MDBInput, MDBFormInline, MDBBtn, MDBIcon } from 'mdbreact';

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    };
  }

  render() {
    return (
      <div className="mb-4" style={{ marginTop: '70px' }}>

        <h2 className="h1-responsive text-center my-4">Order management</h2>

        <MDBFormInline>
          <div class="form-row col-12 p-0 " style={{ border: '1px solid ' }}>
            <div className='col-1 p-0' style={{ border: '1px solid red', marginRight: '2px' }}>
              <label style={{ border: '1px solid pink', fontWeight: 'bold', padding:'0px',textAlign}}>Order:</label>
            </div>
            <MDBInput style={{ border: '1px solid '}} label="Id" outline disabled />
            <MDBInput label="Source" outline />
            <MDBInput label="Status" outline />
            <MDBInput label="Tracking Id" outline />
            <MDBInput label="Total" outline disabled />
          </div>
          <div class="form-row col-12 p-0" style={{ border: '' }}>
            <div className='col-1 text-left' style={{ border: '1px solid red', marginRight: '2px' }}>
              <label style={{ border: '1px solid pink', fontWeight: 'bold', marginRight: '2px' }}>Customer:</label>
            </div>
            <MDBInput label="Name" outline />
            <MDBInput label="Contact" outline />
            <MDBInput label="Address" outline />
            <MDBInput label="Total value added" outline disabled />
          </div>
          <div class="form-row col-12 p-0" style={{ border: '' }}>
            <div className='col-1' style={{ border: '1px solid red', marginRight: '2px' }}>
              <label style={{ border: '1px solid pink', fontWeight: 'bold', marginRight: '2px' }}>Product:</label>
            </div>
            <MDBInput type='text' label="Name" outline required />
            <MDBInput type='text' label="SKU" outline />
            <MDBInput type='number' label="Rate" outline min='0' />
            <MDBInput type='number' label="Qty" outline />
            <MDBInput type='number' label="Price" outline />
            <MDBInput type='number' label="Remarks" outline />
            <MDBInput style={{ marginLeft: '5px' }} type='checkbox' filled label='Is Extra added?' />
            <MDBBtn size='sm' outline>+</MDBBtn>



          </div>
        </MDBFormInline>

      </div>
    );
  }
}

export default Home;
