import React, { Component } from 'react';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBCol, MDBRow, MDBModalHeader, MDBModalFooter, MDBIcon } from
    'mdbreact';

class DeleteOrderModal extends Component {
    state = {
        modal6: false
    }

    toggle = nr => () => {
        let modalNumber = 'modal' + nr
        this.setState({
            [modalNumber]: !this.state[modalNumber]
        });
    }

    render() {
        return (
            <MDBContainer>
                <MDBModal modalStyle="danger" className="text-white" size="sm" centered backdrop={false} isOpen={this.props.isOpen}
                    toggle={this.props.toggle('delete')}>
                    <MDBModalHeader className="text-center" titleClass="w-100" tag="p" toggle={this.props.toggle('delete')}>
                        Are you sure?
                    </MDBModalHeader>
                    <MDBModalBody className="text-center">
                        <MDBIcon icon="times" size="4x" className="animated rotateIn" />
                    </MDBModalBody>
                    <MDBModalFooter className="justify-content-center">
                        <MDBBtn color="danger" onClick={this.props.delete}>Yes</MDBBtn>
                        <MDBBtn color="danger" outline onClick={this.props.toggle('delete')}>No</MDBBtn>
                    </MDBModalFooter>
                </MDBModal>
            </MDBContainer>
        );
    }
}

export default DeleteOrderModal;
