import React, { Component } from 'react';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBCol, MDBRow, MDBModalHeader, MDBModalFooter, MDBIcon } from
    'mdbreact';

class deleteOrderModal extends Component {
    state = {
        deleteModal: false
    }

    toggle = () => {
        this.setState({
            deleteModal: !this.state.deleteModal
        });
    }

    render() {
        return (
            <MDBContainer>
                <MDBBtn onClick={this.toggle}>Launch MDBModal</MDBBtn>
                <MDBModal modalStyle="danger" className="text-white" size="sm" side position="top-right" backdrop={false} isOpen={this.state.deleteModal}
                    toggle={this.toggle}>
                    <MDBModalHeader className="text-center" titleClass="w-100" tag="p" toggle={this.toggle}>
                        Are you sure?
                    </MDBModalHeader>
                    <MDBModalBody className="text-center">
                        <MDBIcon icon="times" size="4x" className="animated rotateIn" />
                    </MDBModalBody>
                    <MDBModalFooter className="justify-content-center">
                        <MDBBtn color="danger" onClick={this.toggle}>Yes</MDBBtn>
                        <MDBBtn color="danger" outline onClick={this.toggle(6)}>No</MDBBtn>
                    </MDBModalFooter>
                </MDBModal>
            </MDBContainer>
        );
    }
}

export default deleteOrderModal;
