import React, { Component } from 'react';
import { MDBDataTable, MDBCard, MDBCardHeader, MDBCardBody, MDBBtn, MDBIcon } from 'mdbreact';
import { Link } from 'react-router-dom'
import ViewOrderModal from './sections/ViewOrderModal';
import EditOrderModal from './sections/EditOrderModal';
import DeleteOrderModal from './sections/DeleteOrderModal';


class Home extends Component {
    constructor() {
        super();
        fetch('/getAllOrders',
        )
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
                // if (this._isMounted) {
                this.setState({ orders: json.data })
                // }
            })
            .catch((error) => console.log(error))
        this.state = {
            orders: [],
        }
        this.refreshOrdersTable = this.refreshOrdersTable.bind(this);
    }
    handleView = () => {
        console.log('view');
        this.refs.viewOrderModal.setState({
            modalShow: true

        })

    }

    handleEdit = () => {
        console.log('edit');
        var iconClik = e.target.parentNode.parentNode.parentNode.parentNode;
        var svgClik = e.target.parentNode.parentNode.parentNode;
        var buttonClik = e.target.parentNode.parentNode;
        var tdClik = e.target.parentNode;
        var targt = e.target

        if (iconClik.rowIndex === undefined) {
            iconClik = svgClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = buttonClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = tdClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = targt;
        }
        let id = iconClik.cells[1].innerHTML
        this.refs.editOrderModal.setState({
            modalShow: true,
            orderId:id


        })

    }

    handleDelete = (e) => {
        var iconClik = e.target.parentNode.parentNode.parentNode.parentNode;
        var svgClik = e.target.parentNode.parentNode.parentNode;
        var buttonClik = e.target.parentNode.parentNode;
        var tdClik = e.target.parentNode;
        var targt = e.target

        if (iconClik.rowIndex === undefined) {
            iconClik = svgClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = buttonClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = tdClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = targt;
        }
        var i = iconClik.rowIndex;
        let dRowValue = iconClik.cells[1].innerHTML

        this.refs.deleteOrderModal.setState({
            modalShow: true,
            rowToBeDeleted: i,
            dRowValue: dRowValue

        })
    }


    refreshOrdersTable = () => {

    }


    render() {
        var orders = this.state.orders;
        var rows = [];
        var index = 0;
        orders.forEach((order) => {

            index = index + 1;
            rows.push(
                {
                    index: index,
                    id: order.id,
                    cName: order.customer_name,
                    cContact: order.customer_contact,
                    cAddress: order.customer_addres,
                    total: order.total,
                    totalValueAdded: order.total_value_added,
                    tracking: order.tracking_id,
                    note: order.note,
                    status: order.status,
                    buttons: <React.Fragment>
                        <MDBBtn onClick={this.handleView} className='m-1 p-2' outline color='info' size="sm"><MDBIcon icon="eye" className='mr-1' />view</MDBBtn>
                        <MDBBtn onClick={this.handleEdit} className='m-1 p-2' outline color='teal' size="sm"><MDBIcon icon="pencil-alt" className='mr-1' />edit</MDBBtn>
                        <MDBBtn onClick={this.handleDelete} className='m-1 p-2' outline color='red darken-3' size="sm"><MDBIcon icon="trash" className="mr-1" />delete</MDBBtn>
                    </React.Fragment>
                }
            );
        });

        var data = {
            columns: [
                { label: '#', field: 'index', }, { label: 'Id', field: 'id', }, { label: 'Customer Name', field: 'cName' },
                { label: 'Customer Contact', field: 'cContact', }, { label: 'Customer Address', field: 'cAddress', },
                { label: 'Total', field: 'total', }, { label: 'Total Value Added', field: 'totalValueAdded', },
                { label: 'Tracking Id', field: 'tracking', }, { label: 'Note', field: 'note', },
                { label: 'Status', field: 'status', }, { label: '', field: 'buttons' }
            ],
            rows: rows
        }
        return (

            <MDBCard className=' p-0' style={{ marginTop: '70px' }}>
                <MDBCardHeader tag="h4" className="text-center font-weight-bold">
                    Orders
                </MDBCardHeader>
                <MDBCardBody className='p-2'>
                    <MDBBtn className='m-0 font-weight-bold' color='secondary' >
                        <Link to='/newOrder' style={{ color: 'white' }}>
                            Add New Order
                        </Link>
                    </MDBBtn>
                    <MDBDataTable id='ordersTable' striped small hover theadColor="teal"
                        bordered btn entries={5} entriesOptions={[5, 10, 20, 35, 55, 70, 100, 135]} responsive
                        data={data} theadTextWhite >
                    </MDBDataTable>
                    <ViewOrderModal
                        ref='viewOrderModal'
                    />
                    <EditOrderModal
                        ref='editOrderModal'
                    />
                    <DeleteOrderModal
                        ref='deleteOrderModal'
                    />
                    {/* <MDBTableHead color='teal' textWhite >
                        <tr>
                            <th>Sr.</th>
                            <th>Id</th>
                            <th>Customer Name</th>
                            <th>Customer Contact</th>
                            <th>Customer Address</th>
                            <th>Total</th>
                            <th>Total Value Added</th>
                            <th>Tracking Id</th>
                            <th>Note</th>
                            <th>Status</th>
                        </tr>
                    </MDBTableHead>
                    <MDBTableBody>
                        {rows}
                    </MDBTableBody> */}
                </MDBCardBody>
            </MDBCard>
        );
    }

}

export default Home
