import React, { Component } from 'react';
import { MDBDataTable, MDBCard, MDBCardHeader, MDBCardBody, MDBBtn, MDBIcon, MDBContainer, MDBRow, MDBCol, MDBInput } from 'mdbreact';
import { Can } from "../../../configs/Ability-context";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import 'react-datepicker/dist/react-datepicker-cssmodules.css';


class AllReports extends Component {
    _isMounted = false
    constructor() {
        super();
        this._isMounted = true
        fetch('/getAllOrderDetails')
            .then((res) => res.json())
            .then((json) => {
                console.log(json)
                let orderDetails = json.data
                // orderDetails.forEach(x => { console.log(x.order_date); }
                // )
                // orderDetails.map((x) => {
                //     delete x.createdAt;
                //     delete x.updatedAt;
                //     delete x.deletedAt;
                //     delete x.id;
                //     delete x.order_id;
                //     if (x.order_date === null) {
                //         x.order_date = new Date("2000-01-01T00:00:00.000Z").toLocaleDateString()
                //     }
                //     else {
                //         console.log(x.order_date);

                //         x.order_date = new Date(x.order_date).toLocaleDateString()
                //         console.log(x.order_date);
                //     }
                // });
                if (this._isMounted) {
                    this.setState({ orderDetails: orderDetails })
                }
            })
            .catch((error) => console.log(error))
        this.state = {
            orderDetails: [],
            fromDate: new Date()
        }
    }

    componentWillUnmount = () => {
        this._isMounted = false
    }

    // handling date value

    handleDateChange = (value) => {
        this.setState({
            fromDate: value
        });
        let selectedOrderDetails = this.state.orderDetails.filter(orderDetail => new Date(orderDetail.order_date) > value)
        console.log(value);
        console.log(new Date(1 / 1 / 1900));
        console.log(selectedOrderDetails);
        this.setState({
            orderDetails: selectedOrderDetails
        })

    }

    totalSales = () => {
        let Total = 0;
        this.state.orderDetails.forEach(orderDetail => {
            Total = Total + Number(orderDetail.product_price)
        })
        return Total
    }

    totalValueAdded = () => {
        let Total = 0;
        let details = this.state.orderDetails.filter(orderDetail => orderDetail.product_extra_added === 'Yes')
        details.forEach(detail => {
            Total = Total + Number(detail.product_price)
        })
        return Total

    }

    render() {
        var { orderDetails, fromDate } = this.state;
        var rows = [];
        var index = 0;
        orders.forEach((order) => {

            index = index + 1;
            let orderDate;
            if (order.date === null) {
                orderDate = '';
            }
            else {
                console.log(order.date);

                orderDate = new Date(order.date).toLocaleDateString();
            }
            rows.push(
                {
                    index: index,
                    order_id: order.order_id,
                    date: orderDate,
                    cName: order.customer_name,
                    cContact: order.customer_contact,
                    cAddress: order.customer_address,
                    total: order.total,
                    tracking: order.tracking_id,
                    note: order.note,
                    status: order.status,
                    buttons: <React.Fragment>
                        <MDBBtn style={{ fontSize: '15px' }} onClick={this.handleView(order.id)} className='m-1 py-1 px-2' outline color='info' size="sm"><MDBIcon icon="eye" /></MDBBtn>
                        <Can I='update' a='order'>
                            <MDBBtn style={{ fontSize: '15px' }} onClick={this.handleEdit(order.id)} className='m-1 py-1 px-2' outline color='teal' size="sm"><MDBIcon icon="pencil-alt" /></MDBBtn>
                        </Can>
                        <Can I='delete' a='order'>
                            <MDBBtn style={{ fontSize: '15px' }} onClick={this.handleDelete(order.id)} className='m-1 py-1 px-2' outline color='red darken-3' size="sm"><MDBIcon icon="trash" /></MDBBtn>
                        </Can>
                    </React.Fragment>
                }
            );
        });

        var data = {
            columns: [
                { label: 'Name', field: 'product_name' },
                { label: 'SKU', field: 'product_sku' }, { label: 'Rate', field: 'product_rate' },
                { label: 'QTY', field: 'product_qty' }, { label: 'Price', field: 'product_price' },
                { label: 'Remarks', field: 'product_remarks' }, { label: 'Extra Added', field: 'product_extra_added' },
                { label: 'Date', field: 'order_date' }
            ],
            rows: orderDetails
        }
        return (
            <Can I='read' a='report'>
                <MDBContainer className='' fluid style={{ marginTop: '80px' }}>
                    <MDBRow center>
                        <MDBCol sm="10">
                            <MDBCard className=' p-0'>
                                <MDBCardHeader tag="h4" style={{ color: 'teal' }} className="text-center font-weight-bold">
                                    Order-details Reports
                            </MDBCardHeader>
                                <MDBCardBody className='p-2'>
                                    <MDBRow className='m-0 p-0' >
                                        <label>Select Date from which you want to fetch report.</label>
                                        <MDBCol md='12' className='m-0 p-0' >
                                            <MDBCol md='3' className='m-0 p-0'>
                                                <DatePicker
                                                    id='datePicker'
                                                    selected={fromDate}
                                                    onChange={this.handleDateChange}
                                                    className='form-control'
                                                    peekNextMonth
                                                    showMonthDropdown
                                                    showYearDropdown
                                                    dropdownMode="select"
                                                    autoComplete='off'
                                                    dateFormat="dd/MM/yy"
                                                />
                                            </MDBCol>
                                        </MDBCol>
                                    </MDBRow>

                                    <MDBDataTable id='reportsTable' striped small hover theadColor="teal"
                                        bordered btn entries={12} entriesOptions={[5, 10, 20, 35, 55, 70, 100, 135]} responsive
                                        data={data} theadTextWhite >
                                    </MDBDataTable>
                                    <MDBRow>
                                        <MDBCol>

                                            <MDBInput type='text' label='Total Sales' value={this.totalSales()} />
                                        </MDBCol>
                                        <MDBCol>
                                            <MDBInput type='text' label='Total Value Added' value={this.totalValueAdded()} />
                                        </MDBCol>
                                    </MDBRow>


                                </MDBCardBody>
                            </MDBCard>
                        </MDBCol>
                    </MDBRow>
                </MDBContainer>
            </Can>
        );
    }

}

export default AllReports
