    { value: 'whatsapp2', label: 'WhatsApp 2' },
    { value: 'phone1', label: 'Phone 1' },
    { value: 'phone2', label: 'Phone 2' },
    { value: 'website', label: 'Website' },
    { value: 'imo', label: 'Imo' },
];


class OrderDetails extends Component {
    constructor(props) {
        super(props);
        this.orderId = '';
        this.state = {
            orderTotal: 0,
            totalValueAdded: 0,
            orderSource: '',
            orderStatus: '',
            orderTracking: '',
            orderNote: '',
            cName: '',
            cContact: '',
            cAddress: '',
            pName: '',
            pSKU: '',
            pRate: '',
            pQTY: '',
            pRemarks: '',
            orderId: 1,
            orderDate: ''
        }
        this.handleSelectChange = this.handleSelectChange.bind(this);
        this.handleInput = this.handleInput.bind(this);
        this.handleAddProduct = this.handleAddProduct.bind(this);
        this.setOrderFormsEmpty = this.setOrderFormsEmpty.bind(this);
        this.orderStatus = this.orderStatus.bind(this)
        this.orderDetailsValidity = this.orderDetailsValidity.bind(this);
        this.handleDateChange = this.handleDateChange.bind(this);
    }

    orderDetailsValidity = () => {
        if (this.state.orderDate === '' || this.state.orderDate === null) {
            this.refs.DatePicker
            return false;
        }
        else if (this.state.orderSource === '' || this.state.orderSource === null) {
            this.setState({
                orderSource: null
            })
            return false;
        }
        else if (this.state.orderStatus === '' || this.state.orderStatus === null) {
            this.refs.createableSelect.setState({ value: null })
            return false;
        }
        else {
            return true;
        }
    }

    saveOrderToDB = () => {
        let date = this.state.orderDate;
        let source = this.state.orderSource.value;
        let status = this.state.orderStatus.value;
        let tracking = this.state.orderTracking;
        let note = this.state.orderNote;
        let total = this.state.orderTotal;
        let totalAddedValue = this.state.totalValueAdded;
        let cName = this.state.cName;
        let cContact = this.state.cContact;
        let cAddress = this.state.cAddress;
        let order = {
            date: date, source: source, status: status, tracking: tracking,
            note: note, total: total, totalAddedValue: totalAddedValue,
            cName: cName, cContact: cContact, cAddress: cAddress
        }
        console.log(date);

        // var options = {
        //     method: 'POST',
        //     body: JSON.stringify(order),
        //     headers: { 'Content-Type': 'application/json' }
        // }
        // fetch('/addNewOrder', options)
        //     .then((res) => res.json())
        //     .then((json) => {
        //         // console.log(json)
        //     })
        //     .catch((error) => console.log(error))
        this.setOrderFormsEmpty();
    }

    orderStatus = (newValue) => {
        this.setState({
            orderStatus: newValue
        })
    }

    handleSelectChange = selectedOption => {

        this.setState({
            orderSource: selectedOption
        })
    }

    setOrderFormsEmpty = () => {
        this.setState({
            cName: '',
            cContact: '',
            cAddress: '',
            orderSource: null,
            orderStatus: null,
            orderTracking: '',
            orderNote: '',
            orderTotal: 0,
            totalValueAdded: 0,
            orderDate: null

        })
        this.refs.createableSelect.setState({ value: null })
    }

    minusTotalValue = (value) => {

        this.setState({
            orderTotal: this.state.orderTotal - parseInt(value)
        })
    }
    minusTotalValueAdded = (value) => {

        this.setState({
            totalValueAdded: this.state.totalValueAdded - parseInt(value)
        })
    }

    addTotalValue = (value) => {

        this.setState({
            orderTotal: this.state.orderTotal + parseInt(value)
        })
    }
    addTotalValueAdded = (value) => {

        this.setState({
            totalValueAdded: this.state.totalValueAdded + parseInt(value)
        })
    }

    handleInput = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleAddProduct = (e) => {
        e.preventDefault();
        let form = this.refs.productForm;
        if (form.checkValidity() === false) {
            form.classList.add('was-validated');
        }
        else {
            document.getElementById('pTableCard').style.display = '';
            let pName = this.state.pName;
            let pSKU = this.state.pSKU;
            let pRate = this.state.pRate;
            let pQTY = this.state.pQTY
            let pPrice = this.pPrice.value;
            let pRemarks = this.state.pRemarks;
            let pChecked = (this.checkbox.checked) ? true : false
            this.props.addProductToTbl(pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked);

            this.addTotalValue(pPrice)
            if (pChecked) {
                this.addTotalValueAdded(pPrice)
            }

            this.setState({
                pName: '',
                pSKU: '',
                pRate: '',
                pQTY: '',
                pRemarks: ''
            })
            this.checkbox.checked = false;
        }
    }

    onKeyPress = (e) => {
        if ((e.which) === 101 || (e.which) === 45) e.preventDefault();
    }

    componentDidMount() {
        this.fetchOrderId();
    }

    fetchOrderId = () => {
        let currentComponent = this;
        fetch('/getLastOrderID')
            .then((res) => res.json())
            .then(function (json) {
                // console.log(json)
                if (json.data.length !== 0) {
                    var lastOrderID = json.data.shift();
                    let id = lastOrderID.id;
                    currentComponent.setState({ orderId: id + 1, showOrderDetails: true })
                }
                else {
                    currentComponent.setState({ orderId: 1, showOrderDetails: true })
                }
            })
            .catch((error) => console.log(error));
    }

    handleDateChange = (value) => {
        // let date = value.toLocaleDateString()
        // let newdate = date.toString('dd/MM/yy')
        this.setState({
            orderDate: value
        });
    }



    render() {

        const { orderSource, orderDate } = this.state
        console.log(orderDate);

        const customStyles = {
            control: (base, state) => ({
                ...base,
                borderColor: state.isFocused ?
                    '#ddd' : orderSource !== null ?
                        '#ddd' : 'red',
                width: '207px',
                // float: 'left'
            })
        }
        return (
            <MDBContainer >
                <MDBRow center>
                    <MDBCol md="9" className='m-0 p-0'>
                        <p className="h4 text-center mb-2">Order Details</p>
                        <MDBRow end className='m-0 p-0' >

                            <MDBCol sm='4' className='m-0'>
                                <MDBInput value={this.state.orderTotal} label="Total" hint="Total" disabled className='m-0' style={{ fontWeight: 'bold' }} />
                            </MDBCol>
                            <MDBCol sm='4' className='m-0'>
                                <MDBInput value={this.state.totalValueAdded} className='m-0' style={{ fontWeight: 'bold' }} label="Total value added" hint="Total value added" disabled />
                            </MDBCol>
                        </MDBRow>
                        <form className='m-0 p-0' ref={this.props.orderForm} noValidate>
                            <fieldset className='legend-border'>
                                <legend className='legend-border'>Order:</legend>
                                <MDBRow className='m-0 p-0'>
                                    <MDBCol size='5' className='m-0' >
                                        <MDBRow >
                                            <MDBCol lg='4' className=' m-0' middle >
                                                <MDBInput className='m-0' value={this.state.orderId} inputRef={this.props.orderId} label="Id" outline disabled />
                                            </MDBCol>
                                            <MDBCol lg='8' className='' middle >
                                                <DatePicker
                                                    selected={this.state.orderDate}
                                                    placeholderText='Date'
                                                    onChange={this.handleDateChange}
                                                    className='form-control datePicker'
                                                    peekNextMonth
                                                    showMonthDropdown
                                                    showYearDropdown
                                                    dropdownMode="select"
                                                    dateFormat="dd/MM/yy"
                                                    customInputRef='datePicker'
                                                />
                                            </MDBCol>
                                        </MDBRow>
                                    </MDBCol>
                                    <MDBCol size='7' className='m-0' middle >
                                        <MDBRow >
                                            <MDBCol lg='6' className=''  >
                                                <React.Fragment>
                                                    <Select
                                                        styles={customStyles}
                                                        value={orderSource}
                                                        onChange={this.handleSelectChange}
                                                        options={sourceOptions}
                                                        placeholder='Source'
                                                        isSearchable
                                                        isClearable
                                                    />
                                                </React.Fragment>
                                            </MDBCol>
                                            <MDBCol lg='6' className=''   >
                                                <React.Fragment>
                                                    <CreateableSelect
                                                        ref='createableSelect'
                                                        orderStatus={this.orderStatus}
                                                    />
                                                </React.Fragment>
                                            </MDBCol>
                                        </MDBRow>
                                    </MDBCol>
                                </MDBRow>
                                <MDBRow className='m-0 p-0' >
                                    <MDBCol sm='4' className='m-0 '>
                                        <MDBInput label="Tracking Id" name="orderTracking" onInput={this.handleInput} value={this.state.orderTracking} outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                    </MDBCol>
                                    <MDBCol sm='8' className='m-0 '>
                                        <MDBInput label="Note" name="orderNote" onInput={this.handleInput} value={this.state.orderNote} outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                    </MDBCol>
                                </MDBRow>
                            </fieldset>
                        </form>
                        <form className='m-0 p-0' ref={this.props.customerForm} >
                            <fieldset className='legend-border'>
                                <legend className='legend-border'>Customer:</legend>

                                <MDBRow className='m-0 p-0'>
                                    <MDBCol sm='4' className='m-0'>
                                        <MDBInput value={this.state.cName} className='mt-0 mb-0' style={{ marginRight: '4px' }} label="Name" name="cName" onInput={this.handleInput} required validate outline />
                                    </MDBCol>
                                    <MDBCol sm='4' className='m-0'>
                                        <MDBInput value={this.state.cContact} className='mt-0 mb-0' style={{ marginRight: '4px' }} label="Contact" name="cContact" onInput={this.handleInput} required validate outline />
                                    </MDBCol>
                                    <MDBCol sm='4' className='m-0'>
                                        <MDBInput value={this.state.cAddress} className='mt-0 mb-0' style={{ marginRight: '4px' }} label="Address" name="cAddress" onInput={this.handleInput} required validate outline />
                                    </MDBCol>

                                </MDBRow>
                            </fieldset>
                        </form>
                        <form className='m-0 p-0' ref='productForm' onSubmit={this.handleAddProduct} noValidate>

                            <fieldset className='legend-border'>
                                <legend className='legend-border'>Product:</legend>
                                <MDBRow className='m-0 p-0'>
                                    <MDBCol sm='4' className='m-0'>
                                        <MDBInput value={this.state.pName} type='text' label="Name" name="pName" onInput={this.handleInput} required validate outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                    </MDBCol>
                                    <MDBCol sm='4' className='m-0'>
                                        <MDBInput value={this.state.pSKU} type='text' label="SKU" name="pSKU" onInput={this.handleInput} outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                    </MDBCol>
                                    <MDBCol sm='4' className='m-0'>
                                        <MDBInput value={this.state.pRate} type='number' label="Rate" name="pRate" onKeyPress={this.onKeyPress} onInput={this.handleInput} required validate outline min='0' className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                    </MDBCol>
                                </MDBRow>
                                <MDBRow className='m-0 p-0'>
                                    <MDBCol sm='4' className='m-0'>
                                        <MDBInput value={this.state.pQTY} type='number' label="Qty" name="pQTY" onKeyPress={this.onKeyPress} onInput={this.handleInput} required validate outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                    </MDBCol>
                                    <MDBCol sm='4' className='m-0'>
                                        <MDBInput value={this.state.pRate * this.state.pQTY} type='number' label='Price' hint="Price" inputRef={e => { this.pPrice = e }} disabled outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                    </MDBCol>
                                    <MDBCol sm='4' className='m-0'>
                                        <MDBInput value={this.state.pRemarks} type='text' label="Remarks" name="pRemarks" onInput={this.handleInput} outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                    </MDBCol>
                                </MDBRow>
                                <MDBRow className='m-0 p-0' end>
                                    <MDBCol sm='4' className='m-0'>
                                        <div className="form-check">
                                            <input className="form-check-input" type="checkbox" ref={e => { this.checkbox = e }} />
                                            <label className="form-check-label" >
                                                Is extra added?
                                    </label>
                                        </div>
                                    </MDBCol>
                                    <MDBCol sm='4' className='m-0' >
                                        <MDBBtn type='submit' size='sm' className='mx-auto' style={{ fontWeight: 'bold', fontSize: '11px' }} outline>Add Product</MDBBtn>
                                    </MDBCol>
                                </MDBRow>

                            </fieldset>
                        </form>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>

        );
    }

}

export default OrderDetails
