import React, { Component } from 'react';
import { MDBDataTable, MDBCard, MDBCardHeader, MDBCardBody } from 'mdbreact';

class Home extends Component {
    constructor() {
        super();
        fetch('/getAllOrders',
        )
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
                // if (this._isMounted) {
                this.setState({ showTable: true, orders: json.data })
                // }
            })
            .catch((error) => console.log(error))
        this.state = {
            orders: [],
        }
        this.refreshOrdersTable = this.refreshOrdersTable.bind(this);
    }

    refreshOrdersTable = () => {
        // this.setState({ showOrders: false })
        // console.log('ok');

        // this.setState({ showOrders: true })
        // this.forceUpdate();
        // console.log('ok');
        // this.setState();
    }


    render() {
        // const searchText = this.props.searchText;
        // const searchFilter = this.props.searchFilter;
        var orders = this.state.orders;
        var rows = [];
        var index = 0;
        orders.forEach((order) => {
            // if (searchResult[searchFilter].indexOf(searchText) === -1) {
            //     return;
            // }
            index = index + 1;
            rows.push(
                // <OTableRow index={index} order={order} key={order.id} />
                {
                    index: index,
                    id: order.id,
                    cName: order.customer_name,
                    cContact: order.customer_contact,
                    cAddress: order.customer_addres,
                    total: order.total,
                    totalValueAdded: order.total_value_added,
                    tracking: order.tracking_id,
                    note: order.note,
                    status: order.status,
                    buttons: ''
                }
            );
        });

        var data = {
            columns: [
                { label: '#', field: 'index', }, { label: 'Id', field: 'id', }, { label: 'Customer Name', field: 'cName' },
                { label: 'Customer Contact', field: 'cContact', }, { label: 'Customer Address', field: 'cAddress', },
                { label: 'Total', field: 'total', }, { label: 'Total Value Added', field: 'totalValueAdded', },
                { label: 'Tracking Id', field: 'tracking', }, { label: 'Note', field: 'note', },
                { label: 'Status', field: 'status', }, { label: '', field: 'buttons' }
            ],
            rows: rows
        }
        return (

            <MDBCard className=' p-0' style={{marginTop:''}}>
                <MDBCardHeader tag="h4" className="text-center font-weight-bold">
                    Orders
                </MDBCardHeader>
                <MDBCardBody className='p-2'>
                    <MDBDataTable id='ordersTable' striped small hover theadColor="teal"
                        bordered btn entries={5} entriesOptions={[5, 10, 15]} responsive
                        data={data} theadTextWhite >
                    </MDBDataTable>
                    {/* <MDBTableHead color='teal' textWhite >
                        <tr>
                            <th>Sr.</th>
                            <th>Id</th>
                            <th>Customer Name</th>
                            <th>Customer Contact</th>
                            <th>Customer Address</th>
                            <th>Total</th>
                            <th>Total Value Added</th>
                            <th>Tracking Id</th>
                            <th>Note</th>
                            <th>Status</th>
                        </tr>
                    </MDBTableHead>
                    <MDBTableBody>
                        {rows}
                    </MDBTableBody> */}
                </MDBCardBody>
            </MDBCard>
        );
    }

}

export default Home
