import React from 'react';

import HeaderNav from './pages/sections/HeaderNav';


class Header extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    };
    this.handleLogOut = this.handleLogOut.bind(this);
  }
  handleLogOut = () => {
    // console.log('ok');
    fetch('/logout')
    .then((res) => res.json())
    .then((json) => {
        console.log(json)
        // hiding some elements
        document.getElementById('homeLink').style.display = 'none'
        document.getElementById('homeLinkCode').style.display = 'none'
        this.setState({ redirect: true, route: json.route })
        this.props.loggedOut();

    })
    .catch((err) => console.log(err))

  }



  render() {
    return (
      <div>
        {this.props.loggedIn ? <HeaderNav handleLogOut={this.handleLogOut} /> : null}
      </div>
    );
  }
}

export default Header
