import React from 'react';
import {
  MDBNavbar, MDBNavbarBrand, MDBNavbarToggler
} from "mdbreact";
import HeaderNav from './pages/sections/HeaderNav';
import { Redirect } from 'react-router-dom';



class Header extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      shouldRedirect: false
    };
  }
  handleLogOut = () => {
    // console.log('ok');
    fetch('/logout')
      .then((res) => res.json())
      .then((json) => {
        console.log(json)
        // hiding some elements
        this.setState({
          shouldRedirect: true
        })
        this.props.loggedOut();

      })
      .catch((err) => console.log(err))

  }

  render() {
    if (this.state.shouldRedirect) {
      return <Redirect to='/login' />
    }
    return (
      <div>
        <header>
          <MDBNavbar color="default-color" dark expand="md" scrolling fixed="top">
            <MDBNavbarBrand>
              <strong className="white-text">Typo</strong>
            </MDBNavbarBrand>
            <MDBNavbarToggler onClick={this.onClick} />
            {this.props.loggedIn ? <HeaderNav handleLogOut={this.handleLogOut} /> : null}
          </MDBNavbar>
        </header>
      </div>
    );
  }
}

export default Header
