import React from 'react';
import ProductsTable from './sections/ProductsTable';
import OrderDetails from './sections/OrderDetails';
import AllOrdersTable from './sections/AllOrdersTable';


class Home extends React.Component {
  constructor(props) {
    super(props);

    this.state = {

    };
    this.addProductToTbl = this.addProductToTbl.bind(this);
    this.deleteProduct = this.deleteProduct.bind(this);
    this.saveOPDetails = this.saveOPDetails.bind(this);
  }

  deleteProduct = (price, i, pChecked) => {

    this.refs.orderDetails.setValue('orderTotal', price);
    if (pChecked == 'Yes') {
      this.refs.orderDetails.setValue('totalValueAdded', price);
    }
    let table = document.getElementById('productsTable');
    table.deleteRow(i);
    if (table.rows.length === 1) {
      this.refs.productsTable.setState({ submitOrderBtn: false })
    }
  }

  addProductToTbl = (pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked) => {
    this.refs.productsTable.addProductToTbl(pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked);
  }

  saveOPDetailsTo = (pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked) => {

    let orderId = this.orderId.value;
    let orderDetails = { order_id: orderId, pName: pName, pSKU: pSKU, pRate: pRate, pQTY: pQTY, pPrice: pPrice, pRemarks: pRemarks, pChecked: pChecked }
    //save to database here
    var options = {
      method: 'POST',
      body: JSON.stringify(orderDetails),
      headers: { 'Content-Type': 'application/json' }
    }

    fetch('/addNewOrderDetails', options)
      .then((res) => res.json())
      .then((json) => {
        console.log(json)
      })
      .catch((error) => console.log(error))

    // console.log(pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked)
    this.refs.orderDetails.setTotalValue(pPrice);
    if (pChecked === 'Yes') {
      this.refs.orderDetails.setTotalValueAdded(pPrice);
    }
    this.refs.orderDetails.setOtherFormsEmpty();
  }

  checkOrderDetailsValidity = () => {
    if (this.orderForm.checkValidity() === false) {
      this.orderForm.classList.add('was-validated');
      return false;
    }
    else if (this.customerForm.checkValidity() === false) {
      this.customerForm.classList.add('was-validated');
      return false;
    }
    else {
      return true;
    }

  }

  render() {

    return (
      <div className="row" style={{ marginTop: '70px' }}>
        <OrderDetails
          ref='orderDetails'
          orderId={el => (this.orderId = el)}
          orderForm={el => (this.orderForm = el)}
          customerForm={el => (this.customerForm = el)}
          addProductToTbl={this.addProductToTbl}
        />
        <div className='col-1' />
        <ProductsTable
          ref='productsTable'
          deleteProduct={this.deleteProduct}
          saveOPDetails={this.saveOPDetails}
          checkOrderDetailsValidity={this.checkOrderDetailsValidity}
        />
        <div className='col-5 mt-4'>
          <AllOrdersTable

          />
        </div>
        <div className='col-1' />

      </div>
    );
  }
}

export default Home;
