import React, { Component } from 'react';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBTable, MDBTableHead, MDBTableBody, MDBModalHeader, MDBModalFooter, MDBRow, MDBCol, MDBInput, MDBDataTable } from 'mdbreact';
import Select from 'react-select';
import CreateableSelect from './CreatableAdvancedSelect';
import PTableRow from './PTableRow';

const sourceOptions = [
    { value: 'facebook', label: 'Facebook' },
    { value: 'whatsapp1', label: 'WhatsApp 1' },
    { value: 'whatsapp2', label: 'WhatsApp 2' },
    { value: 'phone1', label: 'Phone 1' },
    { value: 'phone2', label: 'Phone 2' },
    { value: 'website', label: 'Website' },
    { value: 'imo', label: 'Imo' },
];


class EditOrderModal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            modalShow: false,
            order: '',
            orderTotal: 0,
            totalValueAdded: 0,
            orderSource: '',
            orderStatus: '',
            orderTracking: '',
            orderNote: '',
            cName: '',
            cContact: '',
            cAddress: '',
            orderId: 1,
            rows: [],
            isValidSource: true,
            isValidStatus: true
        }
        this.deleteProduct = this.deleteProduct.bind(this);
    }
    fetchData = (id) => {

        fetch('/getSpecificOrder/' + id)
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
                // this.order = json.data
                var order = json.data
                this.setState({
                    order: order,
                    orderId: order.id,
                    orderTotal: order.total,
                    totalValueAdded: order.total_value_added,
                    orderSource: { label: order.source, value: order.source },
                    orderStatus: { label: order.status, value: order.status },
                    orderTracking: order.tracking_id,
                    orderNote: order.note,
                    cName: order.customer_name,
                    cContact: order.customer_contact,
                    cAddress: order.customer_address,
                    // showOrderDetails: true,
                })
            })
            .catch((error) => console.log(error))

        fetch('/getAllOrderDetails/' + id)
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
                json.data.map((x) => {
                    delete x.createdAt;
                    delete x.updatedAt;
                    delete x.deletedAt;
                    delete x.id;
                    delete x.order_id;
                });
                this.setState({
                    orderDetails: json.data,
                })
                this.setRows(json.data);
            })
            .catch((error) => console.log(error))
    }

    toggle = () => {
        this.setState({
            modalShow: !this.state.modalShow,

        });
    }

    orderStatus = (newValue) => {

   
        this.setState({ orderStatus: newValue })
    }

    handleInput = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleSelectChange = selectedOption => {
        if (selectedOption === null) {
            isValidSource = false
        }
        isValidSource = true
        this.setState({ orderSource: selectedOption })
    }

    setRows = (orderDetails) => {
        this.setState({
            rows: []
        })
        var Rows = [];
        if (orderDetails !== undefined) {
            var index = 0;
            orderDetails.forEach(orderDetail => {
                let pChecked;
                index++;
                if (orderDetail.product_extra_added === "Yes") {
                    pChecked = true
                }
                else {
                    pChecked = false
                }
                Rows.push(
                    <PTableRow
                        index={index}
                        pName={orderDetail.product_name}
                        pSKU={orderDetail.product_sku}
                        pRate={orderDetail.product_rate}
                        pQTY={orderDetail.product_qty}
                        pPrice={orderDetail.product_price}
                        pRemarks={orderDetail.product_remarks}
                        pChecked={pChecked}
                        key={Math.random()}
                        deleteProduct={this.deleteProduct}
                        minusFromTotal={this.minusFromTotal}
                        addToTotal={this.addToTotal}
                        minusFromTotalValueAdded={this.minusFromTotalValueAdded}
                        addToTotalValueAdded={this.addToTotalValueAdded}
                    />
                );
            });
        }
        this.setState({ rows: Rows })
    }

    deleteProduct = (price, i, pChecked) => {
        this.setState({
            orderTotal: this.state.orderTotal - parseInt(price)
        })
        if (pChecked === 'Yes') {
            this.setState({
                totalValueAdded: this.state.totalValueAdded - parseInt(price)
            })
        }
        var rows = [...this.state.rows];
        rows.splice(i - 1, 1);
        this.setState({ rows });
    }

    minusFromTotal = (value) => {
        this.setState({
            orderTotal: this.state.orderTotal - parseInt(value)
        })
    }

    minusFromTotalValueAdded = (value) => {
        this.setState({
            totalValueAdded: this.state.totalValueAdded - parseInt(value)
        })
    }

    addToTotal = (value) => {
        this.setState({
            orderTotal: this.state.orderTotal + parseInt(value)
        })
    }

    addToTotalValueAdded = (value) => {
        this.setState({
            totalValueAdded: this.state.totalValueAdded + parseInt(value)
        })
    }

    updateOrder = () => {
        let form = this.refs.myForm;
        if (form.checkValidity() === false) {
            form.classList.add('was-validated');
            return;
        }
        else if (this.state.orderSource === null) {
            isValidSource = false;
            return;
        }
        else if (this.state.orderStatus === null) {
            isValidStatus = false;
            return;
        }
        else {
            let orderId = this.state.orderId
            let source = this.state.orderSource.value;
            let status = this.state.orderStatus.value;
            let tracking = this.state.orderTracking;
            let note = this.state.orderNote;
            let total = this.state.orderTotal;
            let totalAddedValue = this.state.totalValueAdded;
            let cName = this.state.cName;
            let cContact = this.state.cContact;
            let cAddress = this.state.cAddress;

            var order = { id: orderId, source: source, status: status, tracking: tracking, note: note, total: total, totalAddedValue: totalAddedValue, cName: cName, cContact: cContact, cAddress: cAddress }
            var options = {
                method: 'PUT',
                body: JSON.stringify(order),
                headers: { 'Content-Type': 'application/json' }
            }
            fetch('/updateOrder', options)
                .then((res) => res.json())
                .then((json) => {
                    console.log(json)
                    // let message = json.message;
                    // this.refs.msglabel.innerHTML = message;
                })
                .catch((error) => console.log(error))
            this.toggle()
        }
    }

    render() {
        const { orderSource } = this.state

        const customStyles = {
            control: (base, state) => ({
                ...base,
                borderColor: state.isFocused ?
                    '#ddd' : isValidSource ?
                        '#ddd' : 'red',
                width: '183px'
            })
        }
        return (
            <MDBContainer>
                <MDBModal isOpen={this.state.modalShow} toggle={this.toggle} size='lg' centered>
                    <MDBModalHeader toggle={this.toggle}>Edit Order</MDBModalHeader>
                    <MDBModalBody>

                        <MDBRow center>
                            <MDBCol md="9" className='m-0 p-0'>
                                <MDBRow center className='m-0 p-0' >
                                    <MDBCol sm='6' className='m-0'>
                                        <MDBInput value={this.state.orderTotal} label="Total" hint="Total" disabled className='m-0' style={{ fontWeight: 'bold' }} />
                                    </MDBCol>
                                    <MDBCol sm='6' className='m-0'>
                                        <MDBInput value={this.state.totalValueAdded} className='m-0' style={{ fontWeight: 'bold' }} label="Total value added" hint="Total value added" disabled />
                                    </MDBCol>
                                </MDBRow>
                                <form ref='myForm'>
                                    <MDBRow center className='m-0 p-0' >
                                        <MDBCol sm='4' className='m-0 ' >
                                            <MDBInput value={this.state.orderId} label="Id" outline disabled className='mt-0 mb-0' />
                                        </MDBCol>
                                        <MDBCol md='4' className=' ' middle >
                                            <React.Fragment>
                                                <Select
                                                    styles={customStyles}
                                                    value={orderSource}
                                                    onChange={this.handleSelectChange}
                                                    options={sourceOptions}
                                                    placeholder='Source'
                                                    isSearchable
                                                    className='my-auto'
                                                    isClearable
                                                />
                                            </React.Fragment>
                                        </MDBCol>
                                        <MDBCol md='4' className='m-0 ' middle >
                                            <React.Fragment>
                                                <CreateableSelect
                                                    ref='createableSelect'
                                                    orderStatus={this.orderStatus}
                                                    isValidStatus={isValidStatus}
                                                />
                                            </React.Fragment>
                                        </MDBCol>
                                    </MDBRow>
                                    <MDBRow center className='m-0 p-0' >
                                        <MDBCol sm='4' className='m-0 '>
                                            <MDBInput label="Tracking Id" value={this.state.orderTracking} name="orderTracking" onInput={this.handleInput} outline className='m-0' />
                                        </MDBCol>
                                        <MDBCol sm='8' className='m-0 '>
                                            <MDBInput label="Note" value={this.state.orderNote} name="orderNote" onInput={this.handleInput} outline className='m-0' />
                                        </MDBCol>
                                    </MDBRow>
                                    <MDBRow className='m-0 p-0' end>
                                        <MDBCol sm='12' className='m-0 p-0' bottom >
                                            <label style={{ fontWeight: 'bold', textAlign: 'left' }}>Customer:</label>
                                        </MDBCol>
                                    </MDBRow>
                                    <MDBRow className='m-0 p-0'>
                                        <MDBCol sm='3' className='m-0'>
                                            <MDBInput value={this.state.cName} onInput={this.handleInput} name="cName" className='m-0' label="Name" outline required validate />
                                        </MDBCol>
                                        <MDBCol sm='3' className='m-0'>
                                            <MDBInput value={this.state.cContact} onInput={this.handleInput} name="cContact" className='m-0' label="Contact" outline required validate />
                                        </MDBCol>
                                        <MDBCol sm='6' className='m-0'>
                                            <MDBInput value={this.state.cAddress} onInput={this.handleInput} name="cAddress" className='m-0' label="Address" outline required validate />
                                        </MDBCol>
                                    </MDBRow>
                                    <MDBRow className='m-0 p-0' end>
                                        <MDBCol sm='12' className='m-0 p-0' bottom >
                                            <label style={{ fontWeight: 'bold', textAlign: 'left' }}>Products:</label>
                                        </MDBCol>
                                    </MDBRow>
                                </form>
                                <MDBTable id='editProductsTable' ref='editProductsTable' striped responsive bordered autoWidth>
                                    <caption>
                                        {this.state.submitOrderBtn ?
                                            <MDBBtn onClick={this.submitOrder} type='button' size='sm' className='p-2' >Submit</MDBBtn> :
                                            null}
                                    </caption>
                                    <MDBTableHead color='teal' textWhite >
                                        <tr>
                                            <th>Sr.</th>
                                            <th>Product</th>
                                            <th>SKU</th>
                                            <th>Rate</th>
                                            <th>Qty</th>
                                            <th>Price</th>
                                            <th>Remarks</th>
                                            <th>Extra added</th>
                                            <th>Action</th>
                                        </tr>
                                    </MDBTableHead>
                                    <MDBTableBody>
                                        {this.state.rows}
                                    </MDBTableBody>
                                </MDBTable>

                            </MDBCol>
                        </MDBRow>
                    </MDBModalBody>
                    <MDBModalFooter>
                        <MDBBtn color="secondary" onClick={this.toggle}>Close</MDBBtn>
                        <MDBBtn onClick={this.updateOrder} outline color="primary">Save changes</MDBBtn>
                    </MDBModalFooter>
                </MDBModal>
            </MDBContainer >
        );
    }
}

export default EditOrderModal;
