import React, { Component } from 'react';
import TableRow from './PTableRow';


class ProductsTable extends Component {
    constructor(props) {
        super(props);

        this.state = {
            Rows: [],
        }
        this.addArrayToTbl = this.addArrayToTbl.bind(this);
    }

    addArrayToTbl = (product_id, rate, qty, discount, price) => {
        var row = [];
        row.push(
            <TableRow
                products={this.props.products}
                product_id={product_id}
                rate={rate}
                qty={qty}
                discount={discount}
                price={price}
                EditRow={this.props.EditRow}
                DeleteRow={this.props.DeleteRow}
                key={product_id}
            />
        );
        this.setState(state => {
            var Rows = [...state.Rows, row]
            return {
                Rows
            };
        });

    }

    render() {



        return (
            <MDBCard>
                <MDBCardBody>
                    <MDBTable striped responsive bordered >
                        <MDBTableHead color='teal' textWhite>
                            <tr>
                                <th>Sr.</th>
                                <th>Product</th>
                                <th>Rate</th>
                                <th>QTY</th>
                                <th>Price</th>
                                <th>Remarks</th>
                                <th>Extra Added?</th>
                            </tr>
                        </MDBTableHead>
                        <MDBTableBody>
                            {this.state.Rows}
                        </MDBTableBody>
                    </MDBTable>
                </MDBCardBody>
            </MDBCard>
        );
    }

}

export default ProductsTable
