import React from "react";
import '@fortawesome/fontawesome-free/css/all.min.css';
import { Redirect } from 'react-router'
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn } from 'mdbreact';


class LoginForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            // isLoggedIn: false
        }
        this.handleLogin = this.handleLogin.bind(this);
    }
    handleInput = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    handleLogin = (e) => {
        e.preventDefault();
        let form = this.refs.myForm;
        if (form.checkValidity() === false) {
            form.classList.add('was-validated');
        }
        else {
            // console.log('login bnt ok' + this.state.username + '' + this.state.password)
            // this.setState({ isLoggedIn: true })
            // this.saveToServer();
            var options = {
                method: 'POST',
                body: JSON.stringify({ username: this.state.username, password: this.state.password }),
                headers: {
                    'Content-Type': 'application/json'
                }
            }
            fetch('/auth', options)
                .then((res) => res.json())
                .then((json) => {
                    console.log(json);
                    let message = json.message;
                    this.refs.loginLabel.innerHTML = message;
                    if (message === 'incorrect Password') {
                        this.password.value = "";
                        this.password.focus();
                    }
                    else if (message === `username doesn't exist`) {
                        this.username.value = '';
                        this.username.focus();
                    }
                    else {
                        this.props.history.push('/home')

                    }
                    // this.setState({ isLoggedIn: true })
                    // this.props.changeUserState(json.user.role_id);

                })
                .catch((err) => console.log(err))
        }

    }
    onChangeLabel = () => {
        this.refs.loginLabel.innerHTML = '';
    }


    render() {
        if (this.state.isLoggedIn) {
            return <Redirect to='/home' />
        }
        else
            return (
                <MDBContainer className=" mt-5 pt-5">
                    <MDBRow className=" mt-5 pt-5">
                        <MDBCol md="6" className='mx-auto'>
                            <form ref='myForm' style={{ marginTop: '100px' }} onSubmit={this.handleLogin} noValidate>
                                <p className="h5 text-center mb-4">Sign in</p>
                                <div className="grey-text">
                                    <MDBInput
                                        label="Username"
                                        name="username"
                                        icon="user"
                                        value={this.state.username}
                                        onInput={this.handleInput}
                                        onC
                                        group
                                        type="text"
                                        error="wrong"
                                        success="right"
                                        inputRef={ref => this.username = ref}
                                        required
                                        validate>
                                        <div className="valid-feedback">
                                            Looks good!
                                    </div>
                                        <div className="invalid-feedback">
                                            Please provide a Name.
                                    </div>
                                    </MDBInput>
                                    <MDBInput
                                        label="Password"
                                        name="password"
                                        icon="lock"
                                        value={this.state.password}
                                        onInput={this.handleInput}
                                        inputRef={ref => this.password = ref}
                                        group
                                        type="password"
                                        required
                                        validate>
                                        <div className="valid-feedback">
                                            Looks good!
                                    </div>
                                        <div className="invalid-feedback">
                                            Please provide a Password.
                                    </div>
                                    </MDBInput>
                                </div>
                                <label ref='loginLabel' style={{ color: 'red' }}>hh</label>
                                <div className="text-center">
                                    <MDBBtn type="submit">Login</MDBBtn>
                                </div>
                            </form>
                        </MDBCol>
                    </MDBRow>
                </MDBContainer>
            )

    }
}
export default LoginForm;
