import React, { Component } from 'react';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBCol, MDBRow, MDBModalHeader, MDBModalFooter, MDBIcon } from
    'mdbreact';

class DeleteOrderModal extends Component {
    state = {
        modalShow: false,
        rowToBeDeleted: '',
        dRowValue: ''
    }

    toggle = () => {
        this.setState({
            modalShow: !this.state.modalShow,

        });
    }
    deleteOrder = () => {
        let rowToBeDeleted = this.state.rowToBeDeleted
        let dRowValue = this.state.dRowValue
        document.getElementById('ordersTable').deleteRow(rowToBeDeleted)
        let customer = { value: this.state.dRowValue }

        var options = {
            method: 'DELETE',
            body: JSON.stringify(customer),
            headers: { 'Content-Type': 'application/json' }
        }
        fetch('/deleteOrder', options)
            .then((res) => res.json())
            .then((json) => {
                console.log(json)

            })
            .catch((error) => console.log(error))
    }

    render() {
        return (
            <MDBContainer>
                <MDBModal modalStyle="danger" className="text-white" size="sm" centered backdrop={false} isOpen={this.state.modalShow}
                    toggle={this.toggle}>
                    <MDBModalHeader className="text-center" titleClass="w-100" tag="p" toggle={this.toggle}>
                        Are you sure?
                    </MDBModalHeader>
                    <MDBModalBody className="text-center">
                        <MDBIcon icon="times" size="4x" className="animated rotateIn" />
                    </MDBModalBody>
                    <MDBModalFooter className="justify-content-center">
                        <MDBBtn color="danger" onClick={this.deleteOrder}>Yes</MDBBtn>
                        <MDBBtn color="danger" outline onClick={this.toggle}>No</MDBBtn>
                    </MDBModalFooter>
                </MDBModal>
            </MDBContainer>
        );
    }
}

export default DeleteOrderModal;
