import React, { Component } from 'react';
import PTableRow from './PTableRow';
import { MDBTable, MDBTableHead, MDBTableBody, MDBCard, MDBCardHeader, MDBCardBody, MDBBtn } from 'mdbreact';


class ProductsTable extends Component {
    constructor(props) {
        super(props);

        this.state = {
            Rows: [],
            submitOrderBtn: false,
        }
        this.addProductToTbl = this.addProductToTbl.bind(this);
    }

    addProductToTbl = (pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked) => {

        var row = [];
        row.push(
            <PTableRow
                pName={pName}
                pSKU={pSKU}
                pRate={pRate}
                pQTY={pQTY}
                pPrice={pPrice}
                pRemarks={pRemarks}
                pChecked={pChecked}
                key={Math.random()}
                deleteProduct={this.props.deleteProduct}
            />
        );
        this.setState(state => {
            var Rows = [...state.Rows, row]
            return {
                Rows
            };
        });

    }

    render() {
        if (this.state.submitOrderBtn) {

            this.submitOrderButton.disabled = true;
        }
        else {

            this.submitOrderButton.disabled = false
        }


        return (
            <MDBCard className='col-6 p-0'>
                <MDBCardHeader tag="h3" className="text-center font-weight-bold py-1">
                    Products to be added
                </MDBCardHeader>
                <MDBCardBody className='p-2'>
                    <MDBTable id='productsTable' striped responsive bordered autoWidth scrollY maxHeight='360px'>
                        <caption>
                            <MDBBtn id='submitOrderButton' type='button' size='sm' className='p-2'>Submit</MDBBtn>
                        </caption>
                        <MDBTableHead color='teal' textWhite >
                            <tr>
                                <th>Sr.</th>
                                <th>Product</th>
                                <th>SKU</th>
                                <th>Rate</th>
                                <th>QTY</th>
                                <th>Price</th>
                                <th>Remarks</th>
                                <th>Extra Added?</th>
                                <th></th>
                            </tr>
                        </MDBTableHead>
                        <MDBTableBody>
                            {this.state.Rows}
                        </MDBTableBody>
                    </MDBTable>
                </MDBCardBody>
            </MDBCard>
        );
    }

}

export default ProductsTable
