import React, { Component } from 'react';
import { MDBDataTable, MDBCard, MDBCardHeader, MDBCardBody, MDBBtn, MDBIcon } from 'mdbreact';
import { Link } from 'react-router-dom'
import ViewOrderModal from './sections/ViewOrderModal';
import EditOrderModal from './sections/EditOrderModal';
import DeleteModal from './sections/DeleteModal';


class Home extends Component {
    _isMounted = false
    constructor() {
        super();
        this._isMounted = true
        fetch('/getAllOrders',
        )
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
                if (this._isMounted) {
                    this.setState({ orders: json.data })
                }
            })
            .catch((error) => console.log(error))
        this.state = {
            orders: [],
            rowToBeDeleted: '',
            dRowValue: ''
        }
    }

    componentWillUnmount = () => {
        this._isMounted = false
    }

    handleView = (e) => {
        // console.log(e.target);
        let el = e.target
        let row = el.closest('tr')
        console.log(row.rowIndex);
        
        var iconClik = e.target.parentNode.parentNode.parentNode.parentNode;
        var svgClik = e.target.parentNode.parentNode.parentNode;
        var buttonClik = e.target.parentNode.parentNode;
        var tdClik = e.target.parentNode;
        var targt = e.target

        if (iconClik.rowIndex === undefined) {
            iconClik = svgClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = buttonClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = tdClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = targt;
        }
        console.log(icon);
        
        // let id = iconClik.cells[11].innerHTML
        // this.refs.viewOrderModal.setState({
        //     // orderId: id,
        //     modalShow: true
        // })
        // this.refs.viewOrderModal.fetchData(id);
    }

    handleEdit = (e) => {
        // console.log('edit');
        var iconClik = e.target.parentNode.parentNode.parentNode.parentNode;
        var svgClik = e.target.parentNode.parentNode.parentNode;
        var buttonClik = e.target.parentNode.parentNode;
        var tdClik = e.target.parentNode;
        var targt = e.target

        if (iconClik.rowIndex === undefined) {
            iconClik = svgClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = buttonClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = tdClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = targt;
        }
        let id = iconClik.cells[11].innerHTML

        this.refs.editOrderModal.setState({
            modalShow: true
        })
        this.refs.editOrderModal.fetchData(id);

    }

    handleDelete = (e) => {
        var iconClik = e.target.parentNode.parentNode.parentNode.parentNode;
        var svgClik = e.target.parentNode.parentNode.parentNode;
        var buttonClik = e.target.parentNode.parentNode;
        var tdClik = e.target.parentNode;
        var targt = e.target

        if (iconClik.rowIndex === undefined) {
            iconClik = svgClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = buttonClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = tdClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = targt;
        }
        var i = iconClik.rowIndex;
        let dRowValue = iconClik.cells[11].innerHTML
        this.setState({
            rowToBeDeleted: i,
            dRowValue: dRowValue
        })
        this.refs.deleteModal.setState({
            modalShow: true,

        })
    }

    deleteOrder = () => {
        let rowToBeDeleted = this.state.rowToBeDeleted
        let dRowValue = this.state.dRowValue
        document.getElementById('ordersTable').deleteRow(rowToBeDeleted)
        let order = { value: dRowValue }

        var options = {
            method: 'DELETE',
            body: JSON.stringify(order),
            headers: { 'Content-Type': 'application/json' }
        }
        fetch('/deleteOrder', options)
            .then((res) => res.json())
            .then((json) => {
                console.log(json)
            })
            .catch((error) => console.log(error))
    }
    componentDidMount = () => {
        let table = document.getElementById('ordersTable')
        // console.log(table);
        if (table !== null) {
            console.log(table.rows);
            if (table.children[1].children[2] !== undefined) {
                console.log('ok');

            }
            // console.log(table.rows[1].children);
            // console.log(table.rows[1].cells);
            // console.log(table.rows[0]);
            // console.log(table.rows[1]);
            // console.log(table.rows[1].children[11].style);
            // table.rows[1].children[11].style.display = 'none'
            // table.rows[1].cells[11].style.display = 'none'

        }
    }

    render() {
        var { orders } = this.state;
        var rows = [];
        var index = 0;
        orders.forEach((order) => {

            index = index + 1;
            rows.push(
                {
                    index: index,
                    order_id: order.order_id,
                    date: order.date,
                    cName: order.customer_name,
                    cContact: order.customer_contact,
                    cAddress: order.customer_address,
                    total: order.total,
                    tracking: order.tracking_id,
                    note: order.note,
                    status: order.status,
                    buttons: <React.Fragment>
                        <MDBBtn style={{ fontSize: '15px' }} onClick={this.handleView} className='m-1 py-1 px-2' outline color='info' size="sm"><MDBIcon icon="eye" /></MDBBtn>
                        <MDBBtn style={{ fontSize: '15px' }} onClick={this.handleEdit} className='m-1 py-1 px-2' outline color='teal' size="sm"><MDBIcon icon="pencil-alt" /></MDBBtn>
                        <MDBBtn style={{ fontSize: '15px' }} onClick={this.handleDelete} className='m-1 py-1 px-2' outline color='red darken-3' size="sm"><MDBIcon icon="trash" /></MDBBtn>
                    </React.Fragment>,
                    id: order.id,
                }
            );
        });

        var data = {
            columns: [
                { label: '#', field: 'index', }, { label: 'Order_Id', field: 'order_id', }, { label: 'Date', field: 'date', },
                { label: 'Customer Name', field: 'cName' }, { label: 'Customer Contact', field: 'cContact', },
                { label: 'Customer Address', field: 'cAddress', }, { label: 'Total', field: 'total', },
                { label: 'Tracking Id', field: 'tracking', }, { label: 'Note', field: 'note', },
                { label: 'Status', field: 'status', }, { label: 'Action', field: 'buttons' }, { label: '', field: 'id', },
            ],
            rows: rows
        }


        return (

            <MDBCard className=' p-0' style={{ marginTop: '70px' }}>
                <MDBCardHeader tag="h4" className="text-center font-weight-bold">
                    Orders
                </MDBCardHeader>
                <MDBCardBody className='p-2'>
                    <MDBBtn className='m-0 font-weight-bold' color='secondary' >
                        <Link to='/newOrder' style={{ color: 'white' }}>
                            Add New Order
                        </Link>
                    </MDBBtn>
                    <MDBDataTable id='ordersTable' striped small hover theadColor="teal"
                        bordered btn entries={8} entriesOptions={[10, 20, 35, 55, 70, 100, 135]} responsive
                        data={data} theadTextWhite >
                    </MDBDataTable>
                    <ViewOrderModal
                        ref='viewOrderModal'
                    />
                    <EditOrderModal
                        ref='editOrderModal'
                    />
                    <DeleteModal
                        ref='deleteModal'
                        deleteEntry={this.deleteOrder}
                    />
                </MDBCardBody>
            </MDBCard>
        );
    }

}

export default Home
