import React, { Component } from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput, MDBIcon } from 'mdbreact';
import Select from 'react-select';


const roleOptions = [
    { value: 'admin', label: 'Admin' },
    { value: 'operator', label: 'Operator' }
];

class NewUser extends Component {
    constructor() {
        super();
        this.state = {
            isValidRole: true,
            role: { label: 'Operator', value: 'operator' },
            name: '',
            email: '',
            cell: '',
            username: '',
            password: ''
        };
    }

    handleSelectChange = selectedOption => {
        if (selectedOption === null) {
            this.setState({
                role: selectedOption, isValidRole: false
            })
        }
        else {
            this.setState({
                role: selectedOption, isValidRole: true
            })
        }
    }

    render() {
        const { role, isValidRole } = this.state
        const customStyles = {
            control: (base, state) => ({
                ...base,
                borderColor: state.isFocused ?
                    '#ddd' : isValidRole ?
                        '#ddd' : 'red',
                width: '191px',
            })
        }
        const formatGroupLabel = data => (
            <div style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between',
            }}>
                <span>{data.label}</span>
                <span style={{
                    backgroundColor: '#EBECF0',
                    borderRadius: '2em',
                    color: '#172B4D',
                    display: 'inline-block',
                    fontSize: 12,
                    fontWeight: 'normal',
                    lineHeight: '1',
                    minWidth: 1,
                    padding: '0.16666666666667em 0.5em',
                    textAlign: 'center',
                }}>{data.options.length}</span>
            </div>
        );

        return (

            <MDBContainer className='p-0' style={{ border: '1px solid', marginTop: '80px' }}>
                <MDBRow center>
                    <MDBCol md="6">
                        <form>
                            <p className="h5 text-center mb-4">New User</p>
                            <div className="grey-text">
                                <MDBInput
                                    onInput={this.handleInput}
                                    label="Name"
                                    icon="user"
                                    group
                                    type="text"
                                    validate
                                    error="wrong"
                                    success="right"
                                    required
                                />
                                <MDBInput
                                    onInput={this.handleInput}
                                    label="Email"
                                    icon="envelope"
                                    group
                                    type="email"
                                    validate
                                    error="wrong"
                                    success="right"
                                    required
                                />
                                <MDBInput
                                    onInput={this.handleInput}
                                    label="Phone"
                                    icon="phone"
                                    group
                                    type="text"
                                    validate
                                    error="wrong"
                                    success="right"
                                />
                                <MDBInput
                                    onInput={this.handleInput}
                                    label="Username"
                                    icon="user"
                                    group
                                    type="text"
                                    validate
                                    required
                                />
                                <MDBInput
                                    onInput={this.handleInput}
                                    label="Password"
                                    icon="lock"
                                    group
                                    type="password"
                                    validate
                                    required
                                />
                                {/* <MDBIcon icon="home" style={{float:'left'}} /> */}
                                <Select
                                    styles={customStyles}
                                    def
                                    value={role}
                                    onChange={this.handleSelectChange}
                                    options={roleOptions}
                                    placeholder='Role'
                                    isSearchable
                                    isClearable
                                    formatGroupLabel={formatGroupLabel}
                                />
                            </div>
                            <div className="text-center">
                                <MDBBtn color="primary">Register</MDBBtn>
                            </div>
                        </form>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>

        );
    }
}


export default NewUser
