import React, { Component } from 'react';
import { MDBInput, MDBBtn, MDBCard, MDBCardHeader, MDBCardBody, MDBRow, MDBCol, MDBContainer } from 'mdbreact';
import Select from 'react-select';
import CreateableSelect from './CreatableAdvancedSelect';



const sourceOptions = [
    { value: 'facebook', label: 'Facebook' },
    { value: 'whatsapp1', label: 'WhatsApp 1' },
    { value: 'whatsapp2', label: 'WhatsApp 2' },
    { value: 'phone1', label: 'Phone 1' },
    { value: 'phone2', label: 'Phone 2' },
    { value: 'website', label: 'Website' },
    { value: 'imo', label: 'Imo' },
];
// const statusOptions = [
//     { value: 'pending', label: 'Pending' },
//     { value: 'dispatched', label: 'Dispatched' },
//     { value: 'completed', label: 'Completed' },
// ];
var orderTotal = 0;
var totalValueAdded = 0;
var isValidSource = true;
var isValidStatus = true;
class OrderDetails extends Component {
    constructor(props) {
        super(props);
        this.orderId = '';
        this.state = {
            orderSource: { label: 'Website', value: 'website' },
            orderStatus: { label: 'Pending', value: 'pending' },
            orderTracking: '',
            orderNote: '',
            cName: '',
            cContact: '',
            cAddress: '',
            pName: '',
            pSKU: '',
            pRate: '',
            pQTY: '',
            pRemarks: '',
            orderId: ''
        }
        this.handleSelectChange = this.handleSelectChange.bind(this);
        this.handleInput = this.handleInput.bind(this);
        this.fetchOrderId = this.fetchOrderId.bind(this);
        this.handleAddProduct = this.handleAddProduct.bind(this);
        this.setOrderFormsEmpty = this.setOrderFormsEmpty.bind(this);
        this.orderStatus = this.orderStatus.bind(this)
        this.orderDetailsValidity = this.orderDetailsValidity.bind(this);
    }
    orderDetailsValidity = () => {
        if (this.state.orderSource === null) {
            isValidSource = false
            return false;
        }
        else if (this.state.orderStatus === null) {
            isValidStatus = false
            return false;
        }
        else {
            return true;
        }
    }
    saveOrderToDB = () => {
        let source = this.state.orderSource.value;
        let status = this.state.orderStatus.value;
        let tracking = this.state.orderTracking;
        let note = this.state.orderNote;
        let total = orderTotal;
        let totalAddedValue = totalValueAdded;
        let cName = this.state.cName;
        let cContact = this.state.cContact;
        let cAddress = this.state.cAddress;
        let order = {
            source: source, status: status, tracking: tracking,
            note: note, total: total, totalAddedValue: totalAddedValue,
            cName: cName, cContact: cContact, cAddress: cAddress
        }

        var options = {
            method: 'POST',
            body: JSON.stringify(order),
            headers: { 'Content-Type': 'application/json' }
        }
        fetch('/addNewOrder', options)
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
            })
            .catch((error) => console.log(error))
        this.setOrderFormsEmpty();
    }

    orderStatus = (newValue) => {
        isValidStatus = true
        this.setState({ orderStatus: newValue })

    }

    setOrderFormsEmpty = () => {
        this.setState({
            cName: '',
            cContact: '',
            cAddress: '',
            orderSource: undefined,
            orderStatus: undefined,
            orderTracking: '',
            orderNote: ''
        })
        this.refs.createableSelect.setState({ value: undefined })
    }

    handleSelectChange = selectedOption => {
        isValidSource = true
        this.setState({ orderSource: selectedOption })

    }

    setTotalValue = (value) => {
        console.log(value);
        
        orderTotal -=  parseInt(value)
    }
    setTotalValueAdded = (value) => {
        totalValueAdded -= totalValueAdded - parseInt(value)
    }

    fetchOrderId = () => {
        let currentComponent = this;
        fetch('/getLastOrderID')
            .then((res) => res.json())
            .then(function (json) {
                // console.log(json)
                if (json.data.length != 0) {
                    var lastOrderID = json.data.shift();
                    let id = lastOrderID.id;
                    currentComponent.orderId = id + 1;
                    currentComponent.setState({ orderId: id + 1 })
                }
                else {
                    currentComponent.orderId = 1
                }
            })
            .catch((error) => console.log(error));
    }

    handleInput = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleAddProduct = (e) => {
        e.preventDefault();
        let form = this.refs.productForm;
        if (form.checkValidity() === false) {
            form.classList.add('was-validated');
        }
        else {
            let pName = this.state.pName;
            let pSKU = this.state.pSKU;
            let pRate = this.state.pRate;
            let pQTY = this.state.pQTY
            let pPrice = this.pPrice.value;
            let pRemarks = this.state.pRemarks;
            let pChecked = (this.checkbox.checked) ? true : false
            this.props.addProductToTbl(pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked);
            orderTotal = orderTotal + parseInt(pPrice)
            if (pChecked) {
                totalValueAdded = totalValueAdded + parseInt(pPrice)
            }
            this.setState({
                pName: '',
                pSKU: '',
                pRate: '',
                pQTY: '',
                pRemarks: ''
            })
            this.checkbox.checked = false;
        }
    }
    componentDidMount() {
        this.fetchOrderId();
    }



    render() {

        const { orderSource } = this.state
        const customStyles = {
            control: (base, state) => ({
                ...base,
                borderColor: state.isFocused ?
                    '#ddd' : isValidSource ?
                        '#ddd' : 'red',
                width: '183px'
            })
        }


        return (
            <MDBContainer>
                <MDBRow center>
                    <MDBCol md="9" className='m-0 p-0'>
                        <p className="h4 text-center m-0">Order Details</p>
                        <MDBRow end className='m-0 p-0' >
                            <MDBCol sm='4' className='m-0 p-0 ' bottom >
                                <label className='' style={{ fontWeight: 'bold', textAlign: 'left' }}>Order:</label>
                            </MDBCol>
                            <MDBCol sm='4' className='m-0'>
                                <MDBInput value={orderTotal} label="Total" hint="Total" disabled className='m-0' style={{ fontWeight: 'bold' }} />
                            </MDBCol>
                            <MDBCol sm='4' className='m-0'>
                                <MDBInput value={totalValueAdded} className='m-0' style={{ fontWeight: 'bold' }} label="Total value added" hint="Total value added" disabled />
                            </MDBCol>
                        </MDBRow>
                        <form className='m-0 p-0' ref={this.props.orderForm}>
                            <MDBRow className='m-0 p-0' >
                                <MDBCol sm='3' className='m-0 ' >
                                    <MDBInput value={this.state.orderId} inputRef={this.props.orderId} label="Id" outline disabled className='mt-0 mb-0' />
                                </MDBCol>
                                <MDBCol md='3' className=' ' middle >
                                    <React.Fragment>

                                        <Select
                                            styles={customStyles}
                                            value={orderSource}
                                            onChange={this.handleSelectChange}
                                            options={sourceOptions}
                                            placeholder='Source'
                                            isSearchable
                                            className='my-auto'
                                            isClearable
                                        />
                                    </React.Fragment>
                                </MDBCol>
                                <MDBCol md='3' className='m-0 ' middle >
                                    <React.Fragment>
                                        <CreateableSelect
                                            ref='createableSelect'
                                            orderStatus={this.orderStatus}
                                            isValidStatus={isValidStatus}
                                        />
                                    </React.Fragment>
                                </MDBCol>
                            </MDBRow>
                            <MDBRow className='m-0 p-0' >
                                <MDBCol sm='3' className='m-0 '>
                                    <MDBInput label="Tracking Id" name="orderTracking" onInput={this.handleInput} value={this.state.orderTracking} outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                </MDBCol>
                                <MDBCol sm='3' className='m-0 '>
                                    <MDBInput label="Note" name="orderNote" onInput={this.handleInput} value={this.state.orderNote} outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                </MDBCol>
                            </MDBRow>
                        </form>
                        <form className='m-0 p-0' ref={this.props.customerForm} >
                            <MDBRow className='m-0 p-0' end>
                                <MDBCol sm='12' className='m-0 p-0' bottom >
                                    <label style={{ fontWeight: 'bold', textAlign: 'left' }}>Customer:</label>
                                </MDBCol>
                            </MDBRow>
                            <MDBRow className='m-0 p-0'>
                                <MDBCol sm='3' className='m-0'>
                                    <MDBInput value={this.state.cName} className='mt-0 mb-0' style={{ marginRight: '4px' }} label="Name" name="cName" onInput={this.handleInput} required validate outline />
                                </MDBCol>
                                <MDBCol sm='3' className='m-0'>
                                    <MDBInput value={this.state.cContact} className='mt-0 mb-0' style={{ marginRight: '4px' }} label="Contact" name="cContact" onInput={this.handleInput} required validate outline />
                                </MDBCol>
                                <MDBCol sm='3' className='m-0'>
                                    <MDBInput value={this.state.cAddress} className='mt-0 mb-0' style={{ marginRight: '4px' }} label="Address" name="cAddress" onInput={this.handleInput} required validate outline />
                                </MDBCol>

                            </MDBRow>
                        </form>
                        <form className='m-0 p-0' ref='productForm' onSubmit={this.handleAddProduct} noValidate>
                            <MDBRow className='m-0 p-0' end>
                                <MDBCol sm='12' className='m-0 p-0' bottom >
                                    <label style={{ fontWeight: 'bold', textAlign: 'left' }}>Product:</label>
                                </MDBCol>
                            </MDBRow>
                            <MDBRow className='m-0 p-0'>
                                <MDBCol sm='3' className='m-0'>
                                    <MDBInput value={this.state.pName} type='text' label="Name" name="pName" onInput={this.handleInput} required validate outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                </MDBCol>
                                <MDBCol sm='3' className='m-0'>
                                    <MDBInput value={this.state.pSKU} type='text' label="SKU" name="pSKU" onInput={this.handleInput} outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                </MDBCol>
                                <MDBCol sm='3' className='m-0'>
                                    <MDBInput value={this.state.pRate} type='number' label="Rate" name="pRate" onInput={this.handleInput} required validate outline min='0' className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                </MDBCol>
                            </MDBRow>
                            <MDBRow className='m-0 p-0'>
                                <MDBCol sm='3' className='m-0'>
                                    <MDBInput value={this.state.pQTY} type='number' label="Qty" name="pQTY" onInput={this.handleInput} required validate outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                </MDBCol>
                                <MDBCol sm='3' className='m-0'>
                                    <MDBInput value={this.state.pRate * this.state.pQTY} type='number' label='Price' hint="Price" inputRef={e => { this.pPrice = e }} disabled outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                </MDBCol>
                                <MDBCol sm='3' className='m-0'>
                                    <MDBInput value={this.state.pRemarks} type='text' label="Remarks" name="pRemarks" onInput={this.handleInput} outline className='mt-0 mb-0' style={{ marginRight: '4px' }} />
                                </MDBCol>
                            </MDBRow>
                            <MDBRow className='m-0 p-0' center>
                                <MDBCol sm='3' className='m-0'>
                                    <div className="form-check">
                                        <input className="form-check-input" type="checkbox" ref={e => { this.checkbox = e }} />
                                        <label className="form-check-label" >
                                            Is extra added?
                                    </label>
                                    </div>
                                    {/* <MDBInput type='checkbox' inputRef={e => { this.checkbox = e }} filled label='Is extra added?' /> */}
                                </MDBCol>
                                <MDBCol sm='3' className='m-0' >
                                    <MDBBtn type='submit' size='sm' className='mx-auto' style={{ fontWeight: 'bold', fontSize: '11px' }} outline>Add Product</MDBBtn>
                                </MDBCol>
                            </MDBRow>

                        </form>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>

        );
    }

}

export default OrderDetails
