import React, { Component } from 'react';
import PTableRow from './PTableRow';
import { MDBTable, MDBTableHead, MDBTableBody, MDBCard, MDBCardHeader, MDBCardBody, MDBBtn } from 'mdbreact';


class AllOrdersTable extends Component {
    constructor(props) {
        super(props);

        this.state = {
            Rows: [],
        }
    }

    addProductToTbl = (pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked) => {

        var row = [];
        row.push(
            <PTableRow
                pName={pName}
                pSKU={pSKU}
                pRate={pRate}
                pQTY={pQTY}
                pPrice={pPrice}
                pRemarks={pRemarks}
                pChecked={pChecked}
                key={Math.random()}
                deleteProduct={this.props.deleteProduct}
            />
        );
        this.setState(state => {
            var Rows = [...state.Rows, row]
            return {
                Rows
            };
        });
        this.setState({ submitOrderBtn: true });

    }

    render() {
       

        return (
            <MDBCard className='col p-0'>
                <MDBCardHeader tag="h3" className="text-center font-weight-bold py-1">
                    Orders
                </MDBCardHeader>
                <MDBCardBody className='p-2'>
                    <MDBTable id='productsTable' striped responsive bordered autoWidth scrollY maxHeight='360px'>
                        <caption>
                            {this.state.submitOrderBtn ?
                                <MDBBtn type='button' size='sm' className='p-2' >Submit</MDBBtn> :
                                null}
                        </caption>
                        <MDBTableHead color='teal' textWhite >
                            <tr>
                                <th>Sr.</th>
                                <th>Order.Id</th>
                                <th>SKU</th>
                                <th>Rate</th>
                                <th>QTY</th>
                                <th>Price</th>
                                <th>Remarks</th>
                                <th>Extra Added?</th>
                                <th></th>
                            </tr>
                        </MDBTableHead>
                        <MDBTableBody>
                            {this.state.Rows}
                        </MDBTableBody>
                    </MDBTable>
                </MDBCardBody>
            </MDBCard>
        );
    }

}

export default AllOrdersTable
