import React, { Component } from 'react';


class PTableRow extends Component {
    constructor(props) {
        super(props);

        this.state = {
            pRate: props.pRate,
            pQTY: props.pQTY
        };
        this.onChange = this.onChange.bind(this);
    }



    deleteRowfn(e) {
        let iconClik = e.target.parentNode.parentNode.parentNode.parentNode;
        let svgClik = e.target.parentNode.parentNode.parentNode;
        let buttonClik = e.target.parentNode.parentNode;
        let tdClik = e.target.parentNode;
        let targt = e.target

        if (iconClik.rowIndex === undefined) {
            iconClik = svgClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = buttonClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = tdClik;
        }
        if (iconClik.rowIndex === undefined) {
            iconClik = targt;
        }
        let i = iconClik.rowIndex;
        let price = iconClik.cells[5].innerHTML;
        let pChecked = iconClik.cells[7].innerHTML;

        this.props.deleteProduct(price, i, pChecked);

    }
    getValue = (e) => {
        // return 2;
    }
    onChange = function (e) {
        console.log(e.target.innerHTML);
        console.log(typeof(e.target.innerHTML));
        console.log(typeof(this.state.pRate));

        this.setState({ pRate: e.target.innerHtml },()=>{
            console.log(this.state.pRate);

        })


    }
    getpPrice = () => {
        // console.log(typeof (this.state.pRate));
        // console.log(typeof (parseInt(this.state.pQTY)));
        // console.log(Number(this.state.pRate) * Number(this.state.pQTY));
        console.log(typeof (this.state.pRate));
        console.log(this.state.pRate);
        console.log(typeof (this.state.pQTY));
        console.log(this.state.pQTY);

        var prrice = (parseInt(this.state.pQTY)) * this.state.pRate;
        console.log(typeof (prrice));
        console.log(prrice);

        var ok = prrice.toString();
        console.log(typeof (ok));
        console.log(ok);

        return ok;
    }

    render() {
        var pTable = document.getElementById('productsTable');
        var pName = this.props.pName;
        var pSKU = this.props.pSKU;
        var pRate = this.props.pRate;
        var pQTY = this.props.pQTY;
        var pPrice = this.props.pPrice;
        var pRemarks = this.props.pRemarks;
        var pChecked = (this.props.pChecked) ? 'Yes' : 'No';

        return (
            <tr >
                <td>{'index'}</td>
                <td suppressContentEditableWarning={true} contentEditable='true'>{pName}</td>
                <td suppressContentEditableWarning={true} contentEditable='true'>{pSKU}</td>
                <td suppressContentEditableWarning={true} onBlur={this.onChange} contentEditable='true'>{pRate}</td>
                <td suppressContentEditableWarning={true} contentEditable='true'>{pQTY}</td>
                <td>{this.getpPrice()}</td>
                <td suppressContentEditableWarning={true} contentEditable='true'>{pRemarks}</td>
                <td>{pChecked}</td>
                <td>
                    <button onClick={this.deleteRowfn.bind(this)} type='button' className=" btn-sm btn-dark mb-1">
                        <i className="fas fa-trash"></i>
                    </button>
                </td>
            </tr >
        );
    }
}


export default PTableRow
