import React from 'react';
import {
    MDBNavbarNav, MDBNavItem, MDBNavLink, MDBCollapse, MDBDropdown,
    MDBDropdownToggle, MDBDropdownMenu, MDBIcon
} from "mdbreact";
import { Redirect } from 'react-router-dom';



class Header extends React.Component {
    _isMounted = false
    constructor(props) {
        super(props);
        this.state = {
            collapse: false,
            shouldRedirect: false

        };
        this.onClick = this.onClick.bind(this);
        this.handleLogOut = this.handleLogOut.bind(this);

    }

    handleLogOut = () => {
        // console.log('ok');
        fetch('/logout')
            .then((res) => res.json())
            .then((json) => {
                console.log(json)
                // hiding some elements
                if(this._isMounted){

                    this.setState({
                        shouldRedirect: true
                    })
                }
                this.props.loggedOut();
            })
            .catch((err) => console.log(err))
    }

    onClick() {
        this.setState({
            collapse: !this.state.collapse,
        });
    }

    render() {
        let shouldRedirect = "/login" !== window.location.pathname
        if (this.state.shouldRedirect && shouldRedirect) {
            return <Redirect to='/login' />
        }
        return (

            <MDBCollapse id="navbarCollapse3" isOpen={this.state.collapse} navbar>
                <MDBNavbarNav right id='mainNav'>
                    <MDBNavItem >
                        <MDBNavLink to="/home">Home</MDBNavLink>
                    </MDBNavItem>
                    <MDBNavItem >
                        <MDBNavLink to="/users">Users</MDBNavLink>
                    </MDBNavItem>
                    {/* <MDBNavItem>
                        <MDBDropdown>
                            <MDBDropdownToggle nav caret>
                                <div className="d-none d-md-inline">Dropdown</div>
                            </MDBDropdownToggle>
                            <MDBDropdownMenu className="dropdown-default">
                                <MDBDropdownItem href="#!">Action</MDBDropdownItem>
                                <MDBDropdownItem href="#!">Another Action</MDBDropdownItem>
                                <MDBDropdownItem href="#!">Something else here</MDBDropdownItem>
                                <MDBDropdownItem href="#!">Something else here</MDBDropdownItem>
                            </MDBDropdownMenu>
                        </MDBDropdown>
                    </MDBNavItem>
                    <MDBNavItem>
                        <MDBNavLink to="#">Inventory</MDBNavLink>
                    </MDBNavItem> */}

                </MDBNavbarNav>
                <MDBNavbarNav right id='secondNav'>
                    {/* <MDBNavItem>
                        <MDBNavLink className="waves-effect waves-light" to="#!">
                            <MDBIcon fab icon="twitter" />
                        </MDBNavLink>
                    </MDBNavItem> */}

                    <MDBNavItem id='profileBtn' style={{ display: '' }}>
                        <MDBDropdown>
                            <MDBDropdownToggle nav caret>
                                <MDBIcon icon="user" className="mr-1" />
                            </MDBDropdownToggle>
                            <MDBDropdownMenu className="dropdown-default" right>
                                <MDBNavLink style={{ color: '#008066' }} onClick={this.handleLogOut} to="/login">
                                    LogOut
                                            </MDBNavLink>
                                {/* <MDBDropdownItem href="#!">Another Action</MDBDropdownItem> */}
                            </MDBDropdownMenu>
                        </MDBDropdown>
                    </MDBNavItem>
                </MDBNavbarNav>
            </MDBCollapse>

        );
    }
}

export default Header
