import React, { Component } from 'react';
import { MDBDataTable, MDBCard, MDBCardHeader, MDBCardBody, MDBBtn, MDBIcon, MDBContainer, MDBRow, MDBCol, MDBInput } from 'mdbreact';
import { Can } from "../../../configs/Ability-context";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import 'react-datepicker/dist/react-datepicker-cssmodules.css';


class AllReports extends Component {
    _isMounted = false
    constructor() {
        super();
        this._isMounted = true
        fetch('/getAllOrderDetails')
            .then((res) => res.json())
            .then((json) => {
                console.log(json)
                let orderDetails = json.data
                orderDetails.map((x) => {
                    delete x.createdAt;
                    delete x.updatedAt;
                    delete x.deletedAt;
                    delete x.id;
                    delete x.order_id;
                    if (x.order_date === null) {
                        x.order_date = new Date("1900-01-01T00:00:00.000Z").toLocaleDateString()
                    }
                    else {
                        x.order_date = new Date(x.order_date).toLocaleDateString()
                    }
                });
                if (this._isMounted) {
                    this.setState({ orderDetails: orderDetails })
                }
            })
            .catch((error) => console.log(error))
        this.state = {
            orderDetails: [],
            fromDate: new Date()
        }
    }

    componentWillUnmount = () => {
        this._isMounted = false
    }

    // handling date value

    handleDateChange = (value) => {
        this.setState({
            fromDate: value
        });
        let selectedOrderDetails = this.state.orderDetails.filter(orderDetail=>orderDetail.order_date)
    }

    totalSales = () => {
        let Total = 0;
        this.state.orderDetails.forEach(orderDetail => {
            Total = Total + Number(orderDetail.product_price)
        })
        return Total
    }

    totalValueAdded = () => {
        let Total = 0;
        let details = this.state.orderDetails.filter(orderDetail => orderDetail.product_extra_added === 'Yes')
        details.forEach(detail => {
            Total = Total + Number(detail.product_price)
        })
        return Total

    }

    render() {
        var { orderDetails, fromDate } = this.state;
        var data = {
            columns: [
                { label: 'Name', field: 'product_name' },
                { label: 'SKU', field: 'product_sku' }, { label: 'Rate', field: 'product_rate' },
                { label: 'QTY', field: 'product_qty' }, { label: 'Price', field: 'product_price' },
                { label: 'Remarks', field: 'product_remarks' }, { label: 'Extra Added', field: 'product_extra_added' },
                { label: 'Date', field: 'order_date' }
            ],
            rows: orderDetails
        }
        return (
            <Can I='read' a='report'>
                <MDBContainer className='' style={{ marginTop: '80px' }}>
                    <MDBRow center>
                        <MDBCol md="8">
                            <MDBCard className=' p-0'>
                                <MDBCardHeader tag="h4" style={{ color: 'teal' }} className="text-center font-weight-bold">
                                    Order-details Reports
                            </MDBCardHeader>
                                <MDBCardBody className='p-2'>
                                    <MDBRow className='m-0 p-0' >
                                        <label>Select Date from which you want to fetch report.</label>
                                        <MDBCol md='12' className='m-0 p-0' >
                                            <MDBCol md='3' className='m-0 p-0'>
                                                <DatePicker
                                                    id='datePicker'
                                                    selected={fromDate}
                                                    onChange={this.handleDateChange}
                                                    className='form-control'
                                                    peekNextMonth
                                                    showMonthDropdown
                                                    showYearDropdown
                                                    dropdownMode="select"
                                                    dateFormat="dd/MM/yy"
                                                />
                                            </MDBCol>
                                        </MDBCol>
                                    </MDBRow>

                                    <MDBDataTable id='reportsTable' striped small hover theadColor="teal"
                                        bordered btn entries={12} entriesOptions={[5, 10, 20, 35, 55, 70, 100, 135]} responsive
                                        data={data} theadTextWhite >
                                    </MDBDataTable>
                                    <MDBRow>
                                        <MDBCol>

                                            <MDBInput type='text' label='Total Sales' value={this.totalSales()} />
                                        </MDBCol>
                                        <MDBCol>
                                            <MDBInput type='text' label='Total Value Added' value={this.totalValueAdded()} />
                                        </MDBCol>
                                    </MDBRow>


                                </MDBCardBody>
                            </MDBCard>
                        </MDBCol>
                    </MDBRow>
                </MDBContainer>
            </Can>
        );
    }

}

export default AllReports
