import React, { Component } from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput,MDBCardBody,MDBCardHeader } from 'mdbreact';
import Select from 'react-select';


// const roleOptions = [
//     { value: 'admin', label: 'Admin' },
//     { value: 'operator', label: 'Operator' }
// ];

class NewUser extends Component {
    _isMounted = false
    constructor() {
        super();
        this._isMounted = true
        fetch('/getAllRoles')
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
                if (this._isMounted) {
                    this.setState({ roles: json.data, showOptions: true })
                }
            })
            .catch((error) => console.log(error))
        this.state = {
            role: '',
            name: '',
            email: '',
            cell: '',
            username: '',
            password: '',
            roles: '',
            showOptions: false
        };
    }

    componentWillUnmount() {
        this._isMounted = false
    }

    handleSelectChange = selectedOption => {
        this.setState({
            role: selectedOption
        })
    }

    handleInput = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        let form = this.refs.newUserForm
        if (form.checkValidity() === false) {
            form.classList.add('was-validated');
        }
        else if (this.state.role === '' || this.state.role === null) {
            this.setState({ role: null })
            return
        }
        else {
            let name = this.state.name
            let email = this.state.email
            let cell = this.state.cell
            let username = this.state.username
            let password = this.state.password
            let role = this.state.role.value

            console.log(name, email, cell, username, password, role);
            let user = { name: name, email: email, cell: cell, username: username, password: password, role: role }

            var options = {
                method: 'POST',
                body: JSON.stringify(user),
                headers: { 'Content-Type': 'application/json' }
            }
            fetch('/addNewUser', options)
                .then((res) => res.json())
                .then((json) => {
                    // console.log(json)
                    if (json.success) {
                        this.setState({
                            role: '',
                            name: '',
                            email: '',
                            cell: '',
                            username: '',
                            password: ''
                        })
                    }
                    else {
                        this.refs.username.focus();
                    }
                    this.refs.msglabel.innerHTML = json.message;
                })
                .catch((error) => console.log(error))
        }
    }

    render() {
        const { role, roles, showOptions } = this.state
        const customStyles = {
            control: (base, state) => ({
                ...base,
                borderColor: state.isFocused ?
                    '#ddd' : role !== null ?
                        '#ddd' : 'red',
                width: '191px',
            })
        }
        var roleOptions;
        if (showOptions) {

            roleOptions = roles.map(role => ({ key: role.id, label: role.name, value: role.id }));
        }


        return (

            <MDBContainer className='' fluid style={{ border: '1px solid', marginTop: '80px' }}>
                <MDBCard className=' p-0'>

                    <MDBCardHeader tag="h4" className="text-center font-weight-bold">
                        New User
                </MDBCardHeader>
                    <MDBCardBody className='p-2'>

                        <MDBRow style={{ width: '100%' }} >
                            <MDBCol md="6">
                                <form ref='newUserForm' onSubmit={this.handleSubmit} noValidate>
                                    <p className="h5 text-center mb-4">New User</p>
                                    <div className="grey-text">
                                        <MDBInput
                                            onInput={this.handleInput}
                                            label="Name"
                                            name='name'
                                            icon="user"
                                            group
                                            type="text"
                                            validate
                                            error="wrong"
                                            success="right"
                                            required
                                        />
                                        <MDBInput
                                            onInput={this.handleInput}
                                            label="Email"
                                            name="email"
                                            icon="envelope"
                                            group
                                            type="email"
                                            validate
                                            error="wrong"
                                            success="right"
                                            required
                                        />
                                        <MDBInput
                                            onInput={this.handleInput}
                                            label="Phone"
                                            name="cell"
                                            icon="phone"
                                            group
                                            type="text"
                                            validate
                                            error="wrong"
                                            success="right"
                                        />
                                        <MDBInput
                                            onInput={this.handleInput}
                                            label="Username"
                                            name="username"
                                            ref='username'
                                            icon="user"
                                            group
                                            type="text"
                                            validate
                                            required
                                        />
                                        <MDBInput
                                            onInput={this.handleInput}
                                            label="Password"
                                            name="password"
                                            icon="lock"
                                            group
                                            type="text"
                                            validate
                                            required
                                        />
                                        {/* <MDBIcon icon="home" style={{float:'left'}} /> */}
                                        {showOptions ?
                                            <Select
                                                styles={customStyles}
                                                value={role}
                                                // value={role}
                                                onChange={this.handleSelectChange}
                                                options={roleOptions}
                                                placeholder='Role'
                                                isSearchable
                                                isClearable
                                            /> : null
                                        }
                                    </div>
                                    <div className="text-right">
                                        <MDBBtn size='sm' color="teal" outline type='submit'>Register</MDBBtn>
                                        <label ref='msglabel'>kk</label>
                                    </div>
                                </form>
                            </MDBCol>
                        </MDBRow>
                    </MDBCardBody>
                </MDBCard>
            </MDBContainer>

        );
    }
}


export default NewUser
