import React, { Component } from 'react';
import PTableRow from './PTableRow';
import { MDBTable, MDBTableHead, MDBTableBody, MDBCard, MDBCardHeader, MDBCardBody, MDBBtn } from 'mdbreact';


class AllOrdersTable extends Component {
    constructor() {
        super();
        fetch('/getAllOrders',
        )
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
                if (this._isMounted) {
                    this.setState({ showTable: true })
                }
            })
            .catch((error) => console.log(error))
        this.state = {
            Rows: [],
        }
    }

    addProductToTbl = (pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked) => {

        var row = [];
        row.push(
            <PTableRow
                pName={pName}
                pSKU={pSKU}
                pRate={pRate}
                pQTY={pQTY}
                pPrice={pPrice}
                pRemarks={pRemarks}
                pChecked={pChecked}
                key={Math.random()}
                deleteProduct={this.props.deleteProduct}
            />
        );
        this.setState(state => {
            var Rows = [...state.Rows, row]
            return {
                Rows
            };
        });
        this.setState({ submitOrderBtn: true });

    }

    render() {


        return (
            <MDBCard className='col p-0'>
                <MDBCardHeader tag="h4" className="text-center font-weight-bold">
                    Orders
                </MDBCardHeader>
                <MDBCardBody className='p-2'>
                    <MDBTable id='productsTable' striped responsive bordered autoWidth scrollY maxHeight='360px'>

                        <MDBTableHead color='teal' textWhite >
                            <tr>
                                <th>Sr.</th>
                                <th>Id</th>
                                <th>Customer Name</th>
                                <th>Customer Contact</th>
                                <th>Customer Address</th>
                                <th>Total</th>
                                <th>Total Value Added</th>
                                <th>Tracking Id</th>
                                <th>Note</th>
                                <th>Status</th>
                            </tr>
                        </MDBTableHead>
                        <MDBTableBody>
                            {this.state.Rows}
                        </MDBTableBody>
                    </MDBTable>
                </MDBCardBody>
            </MDBCard>
        );
    }

}

export default AllOrdersTable
