import React, { Component } from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput } from 'mdbreact';
import Select from 'react-select';




class NewUser extends Component {
    constructor() {
        super();
        this.state = {

        };
    }


    render() {
        const { orderSource, isValidSource } = this.state
        const customStyles = {
            control: (base, state) => ({
                ...base,
                borderColor: state.isFocused ?
                    '#ddd' : isValidSource ?
                        '#ddd' : 'red',
                width: '191px',
            })
        }

        return (

            <MDBContainer className='p-0' style={{ border: '1px solid', marginTop: '80px' }}>
                <MDBRow center>
                    <MDBCol md="6">
                        <form>
                            <p className="h5 text-center mb-4">New User</p>
                            <div className="grey-text">
                                <MDBInput
                                    label="Name"
                                    icon="user"
                                    group
                                    type="text"
                                    validate
                                    error="wrong"
                                    success="right"
                                    required
                                />
                                <MDBInput
                                    label="Email"
                                    icon="envelope"
                                    group
                                    type="email"
                                    validate
                                    error="wrong"
                                    success="right"
                                />
                                <MDBInput
                                    label="Phone"
                                    icon="phone"
                                    group
                                    type="text"
                                    validate
                                    error="wrong"
                                    success="right"
                                />
                                <MDBInput
                                    label="Password"
                                    icon="lock"
                                    group
                                    type="password"
                                    validate
                                />
                                <Select
                                    // styles={customStyles}
                                    // value={orderSource}
                                    // onChange={this.handleSelectChange}
                                    options={sourceOptions}
                                    icon='lock'
                                    placeholder='Source'
                                    isSearchable
                                    className='my-auto'
                                    isClearable
                                />
                            </div>
                            <div className="text-center">
                                <MDBBtn color="primary">Register</MDBBtn>
                            </div>
                        </form>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>

        );
    }
}


export default NewUser
