import React, { Component } from 'react';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter, MDBRow, MDBCol, MDBInput, MDBDataTable } from 'mdbreact';

class ViewOrderModal extends Component {
    state = {
        modalShow: false,
        order: ''
    }

    toggle = () => {
        this.setState({
            modalShow: !this.state.modalShow,

        });
    }
    fetchData = (id) => {

        fetch('/getSpecificOrder/' + id)
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
                this.setState({
                    order: json.data
                })
            })
            .catch((error) => console.log(error))

        fetch('/getAllOrderDetails/' + id)
            .then((res) => res.json())
            .then((json) => {
                console.log(json)
                json.data.map((x) => {
                    delete x.createdAt;
                    delete x.updatedAt;
                    delete x.deletedAt;
                    delete x.id;
                    delete x.order_id;
                });
                this.setState({
                    orderDetails: json.data
                })

            })
            .catch((error) => console.log(error))
    }


}

export default ViewOrderModal;
