import React, { Component } from 'react';
import PTableRow from './PTableRow';
import { MDBTable, MDBTableHead, MDBTableBody, MDBCard, MDBCardHeader, MDBCardBody, MDBBtn } from 'mdbreact';


class ProductsTable extends Component {
    constructor(props) {
        super(props);

        this.state = {
            Rows: [],
            submitOrderBtn: false,
        }
        this.addProductToTbl = this.addProductToTbl.bind(this);
    }

    addProductToTbl = (pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked) => {
        var row = [];
        row.push(
            <PTableRow
                pName={pName}
                pSKU={pSKU}
                pRate={pRate}
                pQTY={pQTY}
                pPrice={pPrice}
                pRemarks={pRemarks}
                pChecked={pChecked}
                key={Math.random()}
                deleteProduct={this.props.deleteProduct}
            />
        );
        this.setState(state => {
            var Rows = [...state.Rows, row]
            return {
                Rows
            };
        });
        this.setState({ submitOrderBtn: true });
    }
    submitOrder = () => {
        this.setState({ submitOrderBtn: false })
        let z = this.refs.productsTable;
        const tr = this.refs.functionLayer.state.trType
        const customer_id = this.refs.functionLayer.state.selectedCustomer
        if (tr === 'Sale') {
            for (let index = 1; index < z.rows.length; index++) {
                const productName = z.rows[index].cells[1].innerHTML;
                const productId = z.rows[index].cells[7].innerHTML;
                const rate = z.rows[index].cells[2].innerHTML;
                const qty = z.rows[index].cells[3].innerHTML;
                const discount = z.rows[index].cells[4].innerHTML;
                const price = z.rows[index].cells[5].innerHTML;
                this.saveSalesToServer(customer_id, productId, rate, qty, discount, price, invoice, productName);
            }
            this.saveInvoice(TotalPrice, tr);
            TotalPrice = TotalPrice - TotalPrice;
            for (let index = z.rows.length - 1; index > 0; index--) {
                z.deleteRow(index);
            }
            return;
        }
    }

    render() {


        return (
            <MDBCard className='col-md-5 p-0'>
                <MDBCardHeader tag="h3" className="text-center font-weight-bold py-1">
                    Products to be added
                </MDBCardHeader>
                <MDBCardBody className='p-2'>
                    <MDBTable id='productsTable' striped responsive bordered autoWidth scrollY maxHeight='360px'>
                        <caption>
                            {this.state.submitOrderBtn ?
                                <MDBBtn onClick={this.submitOrder} type='button' size='sm' className='p-2' >Submit</MDBBtn> :
                                null}
                        </caption>
                        <MDBTableHead color='teal' textWhite >
                            <tr>
                                <th>Sr.</th>
                                <th>Product</th>
                                <th>SKU</th>
                                <th>Rate</th>
                                <th>QTY</th>
                                <th>Price</th>
                                <th>Remarks</th>
                                <th>Extra Added?</th>
                                <th></th>
                            </tr>
                        </MDBTableHead>
                        <MDBTableBody>
                            {this.state.Rows}
                        </MDBTableBody>
                    </MDBTable>
                </MDBCardBody>
            </MDBCard>
        );
    }

}

export default ProductsTable
