import React, { Component } from 'react';
import { MDBInput, MDBFormInline, MDBBtn } from 'mdbreact';


var orderTotal = 0;
var totalValueAdded = 0;
class OrderDetails extends Component {
    constructor(props) {
        super(props);
        this.orderTotal = 0;
        this.totalValueAdded = 0;
        this.state = {
            orderId: '',
            orderSource: '',
            orderStatus: '',
            orderTracking: '',
            cName: '',
            cContact: '',
            cAddress: '',
            pName: '',
            pSKU: '',
            pRate: '',
            pQTY: '',
            pRemarks: '',
        }
        this.handleInput = this.handleInput.bind(this);
        this.pTotal = this.pTotal.bind(this);
        this.fetchOrderId = this.fetchOrderId.bind(this);
    }

    fetchOrderId = () => {

    }
    pTotal = () => {
        let total = this.state.pRate * this.state.pQTY;
        return total;
    }

    handleInput = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }



    render() {


        return (
            <MDBCard className='col-6 p-0'>
                <MDBCardHeader tag="h3" className="text-center font-weight-bold py-1">
                    Order Details
                </MDBCardHeader>
                <MDBCardBody className='p-2'>
                    <div ref='mainForm' className='m-0 '>

                        <form className='form-inline' ref='orderForm'>
                            <div className=' p-0' >
                                <label className='' style={{ fontWeight: 'bold', textAlign: 'left' }}>Order:</label>
                            </div>

                            <MDBInput value={this.fetchOrderId()} label="Id" outline disabled style={{ marginRight: '4px' }} />
                            <select name='orderSource' value={this.state.orderSource} onChange={this.handleInput} className=" custom-select" required>
                                <option value='' disabled>--Source--</option>
                                <option value='1' >Option 1</option>
                                <option value='2' >Option 2</option>
                                <option value='3' >Option 3</option>
                            </select>
                            <select name='orderStatus' value={this.state.orderStatus} onChange={this.handleInput} className=" custom-select" required>
                                <option value='' disabled>--Status--</option>
                                <option value='pending' >Pending</option>
                                <option value='dispatched' >Dispatched</option>
                                <option value='completed' >Completed</option>
                            </select>
                            <MDBInput label="Tracking Id" name="orderTracking" onInput={this.handleInput} value={this.state.ord} outline style={{ marginRight: '4px' }} />
                            <MDBInput value={orderTotal} label="Total" hint="Total" outline disabled style={{ marginRight: '4px' }} />
                            <MDBInput value={totalValueAdded} style={{ marginRight: '4px' }} label="Total value added" hint="Total value added" outline disabled />
                        </form>

                        <form className='form-inline' ref='customerForm'>
                            <div className=' p-0' >
                                <label className='' style={{ fontWeight: 'bold', textAlign: 'left' }}>Customer:</label>
                            </div>

                            <MDBInput value={this.state.cName} style={{ marginRight: '4px' }} label="Name" name="cName" onInput={this.handleInput} required validate outline />
                            <MDBInput value={this.state.cContact} style={{ marginRight: '4px' }} label="Contact" name="cContact" onInput={this.handleInput} required validate outline />
                            <MDBInput value={this.state.cAddress} style={{ marginRight: '4px' }} label="Address" name="cAddress" onInput={this.handleInput} required validate outline />
                        </form>
                        <form className='form-inline' ref='productForm' onSubmit={this.handleAddProduct} noValidate>

                            <div className='p-0' >
                                <label className='' style={{ fontWeight: 'bold', textAlign: 'left' }}>Product:</label>
                            </div>

                            <MDBInput value={this.state.pName} type='text' label="Name" name="pName" onInput={this.handleInput} required validate outline style={{ marginRight: '4px' }} />
                            <MDBInput value={this.state.pSKU} type='text' label="SKU" name="pSKU" onInput={this.handleInput} outline style={{ marginRight: '4px' }} />
                            <MDBInput value={this.state.pRate} type='number' label="Rate" name="pRate" onInput={this.handleInput} required validate outline min='0' style={{ marginRight: '4px' }} />
                            <MDBInput value={this.state.pQTY} type='number' label="Qty" name="pQTY" onInput={this.handleInput} required validate outline style={{ marginRight: '4px' }} />
                            <MDBInput value={this.pTotal()} type='number' label='Price' hint="Price" inputRef={e => { this.pPrice = e }} disabled outline style={{ marginRight: '4px' }} />
                            <MDBInput value={this.state.pRemarks} type='text' label="Remarks" name="pRemarks" onInput={this.handleInput} outline style={{ marginRight: '4px' }} />
                            <MDBInput type='checkbox' inputRef={e => { this.checkbox = e }} filled label='Is Extra added?' />
                            <MDBBtn type='submit' size='lg' style={{ width: '50px', height: '40px', padding: '0px' }} outline>Add</MDBBtn>
                        </form>
                    </div>
                </MDBCardBody>
            </MDBCard>
        );
    }

}

export default OrderDetails
