import React, { Component } from 'react';
import { MDBDataTable, MDBCard, MDBCardHeader, MDBCardBody, MDBBtn, MDBIcon, MDBNavLink } from 'mdbreact';
import { Link } from 'react-router-dom'
import ViewOrderModal from './sections/ViewOrderModal';
import EditOrderModal from './sections/EditOrderModal';
import DeleteModal from './sections/DeleteModal';


class Home extends Component {
    _isMounted = false
    constructor() {
        super();
        this._isMounted = true
        fetch('/getAllOrders',
        )
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
                if (this._isMounted) {
                    this.setState({ orders: json.data })
                }
            })
            .catch((error) => console.log(error))
        this.state = {
            orders: [],
            rowToBeDeleted: '',
            dRowValue: ''
        }
    }

    componentWillUnmount = () => {
        this._isMounted = false
    }

    handleView = (id) => e => {
        // let el = e.target
        // let row = el.closest('tr')
        this.refs.viewOrderModal.setState({
            modalShow: true
        })
        this.refs.viewOrderModal.fetchData(id);
    }

    handleEdit = (id) => e => {
        // let el = e.target
        // let row = el.closest('tr')
        this.refs.editOrderModal.setState({
            modalShow: true
        })
        this.refs.editOrderModal.fetchData(id);
    }

    handleDelete = (id) => e => {
        let el = e.target
        let row = el.closest('tr')
        var i = row.rowIndex;
        this.setState({
            rowToBeDeleted: i,
            dRowValue: id
        })
        this.refs.deleteModal.setState({
            modalShow: true,
        })
    }

    deleteOrder = () => {
        let rowToBeDeleted = this.state.rowToBeDeleted
        let dRowValue = this.state.dRowValue
        document.getElementById('ordersTable').deleteRow(rowToBeDeleted)
        let order = { value: dRowValue }

        var options = {
            method: 'DELETE',
            body: JSON.stringify(order),
            headers: { 'Content-Type': 'application/json' }
        }
        fetch('/deleteOrder', options)
            .then((res) => res.json())
            .then((json) => {
                console.log(json)
            })
            .catch((error) => console.log(error))
    }



    render() {
        var { orders } = this.state;
        var rows = [];
        var index = 0;
        orders.forEach((order) => {

            index = index + 1;
            let orderDate = new Date(order.date).toLocaleDateString();
            rows.push(
                {
                    index: index,
                    order_id: order.order_id,
                    date: orderDate,
                    cName: order.customer_name,
                    cContact: order.customer_contact,
                    cAddress: order.customer_address,
                    total: order.total,
                    tracking: order.tracking_id,
                    note: order.note,
                    status: order.status,
                    buttons: <React.Fragment>
                        <MDBBtn style={{ fontSize: '15px' }} onClick={this.handleView(order.id)} className='m-1 py-1 px-2' outline color='info' size="sm"><MDBIcon icon="eye" /></MDBBtn>
                        <MDBBtn style={{ fontSize: '15px' }} onClick={this.handleEdit(order.id)} className='m-1 py-1 px-2' outline color='teal' size="sm"><MDBIcon icon="pencil-alt" /></MDBBtn>
                        <MDBBtn style={{ fontSize: '15px' }} onClick={this.handleDelete(order.id)} className='m-1 py-1 px-2' outline color='red darken-3' size="sm"><MDBIcon icon="trash" /></MDBBtn>
                    </React.Fragment>,
                }
            );
        });

        var data = {
            columns: [
                { label: '#', field: 'index', }, { label: 'Order_Id', field: 'order_id', }, { label: 'Date', field: 'date', },
                { label: 'Customer Name', field: 'cName' }, { label: 'Customer Contact', field: 'cContact', },
                { label: 'Customer Address', field: 'cAddress', }, { label: 'Total', field: 'total', },
                { label: 'Tracking Id', field: 'tracking', }, { label: 'Note', field: 'note', },
                { label: 'Status', field: 'status', }, { label: 'Action', field: 'buttons' }
            ],
            rows: rows
        }


        return (

            <MDBCard className=' p-0' style={{ marginTop: '70px' }}>
                <MDBCardHeader tag="h4" className="text-center font-weight-bold">
                    Orders
                </MDBCardHeader>
                <MDBCardBody className='p-2'>
                    <MDBBtn size='sm' className='m-0 p-0 font-weight-bold' color='info' >
                        <MDBNavLink to='/newOrder' className='m-0' style={{ fontSize: '13px', color: 'white' }}>
                            Add New Order
                        </MDBNavLink>
                    </MDBBtn>
                    <MDBDataTable id='ordersTable' striped small hover theadColor="teal"
                        bordered btn entries={15} entriesOptions={[10, 20, 35, 55, 70, 100, 135]} responsive
                        data={data} theadTextWhite >
                    </MDBDataTable>
                    <ViewOrderModal
                        ref='viewOrderModal'
                    />
                    <EditOrderModal
                        ref='editOrderModal'
                    />
                    <DeleteModal
                        ref='deleteModal'
                        deleteEntry={this.deleteOrder}
                    />
                </MDBCardBody>
            </MDBCard>
        );
    }

}

export default Home
