import React, { Component } from 'react';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBTable, MDBTableHead, MDBTableBody, MDBModalHeader, MDBModalFooter, MDBRow, MDBCol, MDBInput, MDBDataTable } from 'mdbreact';
import Select from 'react-select';
import CreateableSelect from './CreatableAdvancedSelect';
import PTableRow from './PTableRow';

const sourceOptions = [
    { value: 'facebook', label: 'Facebook' },
    { value: 'whatsapp1', label: 'WhatsApp 1' },
    { value: 'whatsapp2', label: 'WhatsApp 2' },
    { value: 'phone1', label: 'Phone 1' },
    { value: 'phone2', label: 'Phone 2' },
    { value: 'website', label: 'Website' },
    { value: 'imo', label: 'Imo' },
];

var isValidSource = true;
var isValidStatus = true;
class EditOrderModal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            modalShow: false,
            order: '',
            orderTotal: 0,
            totalValueAdded: 0,
            orderSource: '',
            orderStatus: '',
            orderTracking: '',
            orderNote: '',
            cName: '',
            cContact: '',
            cAddress: '',
            orderId: 1,
            rows: []
        }
        this.deleteProduct = this.deleteProduct.bind(this);
    }
    fetchData = (id) => {

        fetch('/getSpecificOrder/' + id)
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
                // this.order = json.data
                var order = json.data
                this.setState({
                    order: order,
                    orderId: order.id,
                    orderTotal: order.total,
                    totalValueAdded: order.total_value_added,
                    orderSource: { label: order.source, value: order.source },
                    orderStatus: { label: order.status, value: order.status },
                    orderTracking: order.tracking_id,
                    orderNote: order.note,
                    cName: order.customer_name,
                    cContact: order.customer_contact,
                    cAddress: order.customer_address,
                    // showOrderDetails: true,
                })
            })
            .catch((error) => console.log(error))

        fetch('/getAllOrderDetails/' + id)
            .then((res) => res.json())
            .then((json) => {
                // console.log(json)
                json.data.map((x) => {
                    delete x.createdAt;
                    delete x.updatedAt;
                    delete x.deletedAt;
                    delete x.id;
                    delete x.order_id;
                });
                this.setState({
                    orderDetails: json.data,
                })
                this.setRows(json.data);
            })
            .catch((error) => console.log(error))
    }

    toggle = () => {
        this.setState({
            modalShow: !this.state.modalShow,

        });
    }
    handleInput = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    orderStatus = (newValue) => {

        isValidStatus = true
        this.setState({ orderStatus: newValue })
    }

    handleSelectChange = selectedOption => {

        isValidSource = true
        this.setState({ orderSource: selectedOption })
    }

    deleteProduct = (price, i, pChecked) => {
        this.setState({
            orderTotal: this.state.orderTotal - parseInt(price)
        })
        // this.refs.orderDetails.minusTotalValue(price);
        if (pChecked === 'Yes') {
            this.setState({
                totalValueAdded: this.state.totalValueAdded - parseInt(price)
            })
            //     this.refs.orderDetails.minusTotalValueAdded(price);
        }
        console.log(i);

        var rows = this.state;
        console.log(rows);

        var ok = rows.splice(i, 1);
        console.log(ok);
        console.log(rows);

        this.setState({ rows }, function () {
            console.log(this.state.rows);

        });
        // var table = document.getElementById('editProductsTable');
        // table.deleteRow(i);
        // if (table.rows.length === 1) {
        //     this.refs.productsTable.setState({ submitOrderBtn: false })
        // }

    }
    setRows = (orderDetails) => {
        this.setState({
            rows: []
        })
        var Rows = [];
        if (orderDetails !== undefined) {
            orderDetails.forEach(orderDetail => {
                let pChecked;
                if (orderDetail.product_extra_added === "Yes") {
                    pChecked = true
                }
                else {
                    pChecked = false
                }
                Rows.push(
                    <PTableRow
                        pName={orderDetail.product_name}
                        pSKU={orderDetail.product_sku}
                        pRate={orderDetail.product_rate}
                        pQTY={orderDetail.product_qty}
                        pPrice={orderDetail.product_price}
                        pRemarks={orderDetail.product_remarks}
                        pChecked={pChecked}
                        key={Math.random()}
                        deleteProduct={this.deleteProduct}
                    // minusFromTotal={this.props.minusFromTotal}
                    // addToTotal={this.props.addToTotal}
                    // minusFromTotalValueAdded={this.props.minusFromTotalValueAdded}
                    // addToTotalValueAdded={this.props.addToTotalValueAdded}
                    />
                );
            });
        }
        this.setState(state => {
            var rows = [...state.rows, Rows]
            return {
                rows
            };
        })
    }


    render() {
        const { orderSource } = this.state

        const customStyles = {
            control: (base, state) => ({
                ...base,
                borderColor: state.isFocused ?
                    '#ddd' : isValidSource ?
                        '#ddd' : 'red',
                width: '183px'
            })
        }
        return (
            <MDBContainer>
                <MDBModal isOpen={this.state.modalShow} toggle={this.toggle} size='lg' centered>
                    <MDBModalHeader toggle={this.toggle}>Edit Order</MDBModalHeader>
                    <MDBModalBody>

                        <MDBRow center>
                            <MDBCol md="9" className='m-0 p-0'>
                                <MDBRow center className='m-0 p-0' >
                                    <MDBCol sm='6' className='m-0'>
                                        <MDBInput value={this.state.orderTotal} label="Total" hint="Total" disabled className='m-0' style={{ fontWeight: 'bold' }} />
                                    </MDBCol>
                                    <MDBCol sm='6' className='m-0'>
                                        <MDBInput value={this.state.totalValueAdded} className='m-0' style={{ fontWeight: 'bold' }} label="Total value added" hint="Total value added" disabled />
                                    </MDBCol>
                                </MDBRow>
                                <MDBRow center className='m-0 p-0' >
                                    <MDBCol sm='4' className='m-0 ' >
                                        <MDBInput value={this.state.orderId} label="Id" outline disabled className='mt-0 mb-0' />
                                    </MDBCol>
                                    <MDBCol md='4' className=' ' middle >
                                        <React.Fragment>
                                            <Select
                                                styles={customStyles}
                                                value={orderSource}
                                                onChange={this.handleSelectChange}
                                                options={sourceOptions}
                                                placeholder='Source'
                                                isSearchable
                                                className='my-auto'
                                                isClearable
                                            />
                                        </React.Fragment>
                                    </MDBCol>
                                    <MDBCol md='4' className='m-0 ' middle >
                                        <React.Fragment>
                                            <CreateableSelect
                                                ref='createableSelect'
                                                orderStatus={this.orderStatus}
                                                isValidStatus={isValidStatus}
                                            />
                                        </React.Fragment>
                                    </MDBCol>
                                </MDBRow>
                                <MDBRow center className='m-0 p-0' >
                                    <MDBCol sm='4' className='m-0 '>
                                        <MDBInput label="Tracking Id" value={this.state.orderTracking} name="orderTracking" onInput={this.handleInput} outline className='m-0' />
                                    </MDBCol>
                                    <MDBCol sm='8' className='m-0 '>
                                        <MDBInput label="Note" value={this.state.orderNote} name="orderNote" onInput={this.handleInput} outline className='m-0' />
                                    </MDBCol>
                                </MDBRow>
                                <MDBRow className='m-0 p-0' end>
                                    <MDBCol sm='12' className='m-0 p-0' bottom >
                                        <label style={{ fontWeight: 'bold', textAlign: 'left' }}>Customer:</label>
                                    </MDBCol>
                                </MDBRow>
                                <MDBRow className='m-0 p-0'>
                                    <MDBCol sm='3' className='m-0'>
                                        <MDBInput value={this.state.cName} onInput={this.handleInput} name="cName" className='m-0' label="Name" outline required />
                                    </MDBCol>
                                    <MDBCol sm='3' className='m-0'>
                                        <MDBInput value={this.state.cContact} onInput={this.handleInput} name="cContact" className='m-0' label="Contact" outline required />
                                    </MDBCol>
                                    <MDBCol sm='6' className='m-0'>
                                        <MDBInput value={this.state.cAddress} onInput={this.handleInput} name="cAddress" className='m-0' label="Address" outline required />
                                    </MDBCol>

                                </MDBRow>
                                <MDBRow className='m-0 p-0' end>
                                    <MDBCol sm='12' className='m-0 p-0' bottom >
                                        <label style={{ fontWeight: 'bold', textAlign: 'left' }}>Products:</label>
                                    </MDBCol>
                                </MDBRow>
                                <MDBTable id='editProductsTable' ref='editProductsTable' striped responsive bordered autoWidth>
                                    <caption>
                                        {this.state.submitOrderBtn ?
                                            <MDBBtn onClick={this.submitOrder} type='button' size='sm' className='p-2' >Submit</MDBBtn> :
                                            null}
                                    </caption>
                                    <MDBTableHead color='teal' textWhite >
                                        <tr>
                                            <th>Sr.</th>
                                            <th>Product</th>
                                            <th>SKU</th>
                                            <th>Rate</th>
                                            <th>Qty</th>
                                            <th>Price</th>
                                            <th>Remarks</th>
                                            <th>Extra added</th>
                                            <th></th>
                                        </tr>
                                    </MDBTableHead>
                                    <MDBTableBody>
                                        {this.state.rows}
                                    </MDBTableBody>
                                </MDBTable>

                            </MDBCol>
                        </MDBRow>
                    </MDBModalBody>
                    <MDBModalFooter>
                        <MDBBtn color="secondary" onClick={this.toggle}>Close</MDBBtn>
                        <MDBBtn outline color="primary">Save changes</MDBBtn>
                    </MDBModalFooter>
                </MDBModal>
            </MDBContainer >
        );
    }
}

export default EditOrderModal;
