import React, { Component } from 'react';
import PTableRow from './PTableRow';
import { MDBTable, MDBTableHead, MDBTableBody, MDBCard, MDBCardHeader, MDBCardBody } from 'mdbreact';


class ProductsTable extends Component {
    constructor(props) {
        super(props);

        this.state = {
            Rows: [],
        }
        this.addProductToTbl = this.addProductToTbl.bind(this);
    }

    addProductToTbl = (pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked) => {
        console.log(pName, pSKU, pRate, pQTY, pPrice, pRemarks, pChecked);

        var row = [];
        row.push(
            <PTableRow
                pName={pName}
                pSKU={pSKU}
                pRate={pRate}
                pQTY={pQTY}
                pPrice={pPrice}
                pRemarks={pRemarks}
                pChecked={pChecked}
            />
        );
        this.setState(state => {
            var Rows = [...state.Rows, row]
            return {
                Rows
            };
        });

    }

    render() {



        return (
            <MDBCard className='col-6 p-0'>
                <MDBCardHeader tag="h3" className="text-center font-weight-bold py-2">
                    Products to be added
                </MDBCardHeader>
                <MDBCardBody className='p-2'>
                    <MDBTable striped responsive bordered >
                        <MDBTableHead color='teal' textWhite>
                            <tr>
                                <th>Sr.</th>
                                <th>Product</th>
                                <th>SKU</th>
                                <th>Rate</th>
                                <th>QTY</th>
                                <th>Price</th>
                                <th>Remarks</th>
                                <th>Extra Added?</th>
                                <th></th>
                            </tr>
                        </MDBTableHead>
                        <MDBTableBody>
                            {this.state.Rows}
                        </MDBTableBody>
                    </MDBTable>
                </MDBCardBody>
            </MDBCard>
        );
    }

}

export default ProductsTable
